import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'tagEsc',
})
export class TagEscPipe implements PipeTransform {
  transform(value: string, args?: any): any {
    if (value) return value.replace(/</g, '&lt;').replace(/>/g, '&gt;').replace(/\\n/g, '<br />');
  }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'toDataSource'
})
export class ToDataSourcePipe implements PipeTransform {

  transform(model: { [key: string]: any }): { results: any[]; count: number; } {
    if (!model) {
      return { results: [], count: 0 };
    }

    const results = Object.entries(model)
      .reduce((acc, [key, value]) => {
        return [...acc, { key, value }];
      }, []);

    return { results, count: 0 };
  }

}

import { Pipe, PipeTransform } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';

@Pipe({
  name: 'translateKeys'
})
export class TranslateKeysPipe implements PipeTransform {

  constructor(private _translate: TranslateService) {}

  transform(object: { [key: string]: any }): { [key: string]: any } {
    if (!object) {
      return {};
    }

    return Object.entries(object)
      .reduce((acc, [key, value]) => {
        return {
          ...acc,
          [this._translate.instant(key)]: value
        }
      }, {});
  }

}

