import { IRoot } from '@app/core/types/api';
import { Action, createAction, createFeatureSelector, createReducer, createSelector, on, props } from '@ngrx/store';

export const loadRoot = createAction('[API] LoadRoot');
export const loadStack = createAction('[API] LoadStack');
export const rootSuccess = createAction('[API] LoadRootSuccess', props<{ root: IRoot }>());
export const rootError = createAction('[API] LoadRootError');
export const stackSuccess = createAction('[API] LoadStackSuccess', props<{ stack: IRoot }>());

export interface ApiState {
  root: IRoot;
  stack: IRoot;
}

const InitState: ApiState = {
  root: null,
  stack: null,
};

const reducer = createReducer(
  InitState,
  on(rootSuccess, (state, { root }) => ({ ...state, root })),
  on(rootError, state => ({ root: null, stack: null })),
  on(stackSuccess, (state, { stack }) => ({ ...state, stack }))
);

export function apiReducer(state: ApiState, action: Action) {
  return reducer(state, action);
}

export const getApiState = createFeatureSelector<ApiState>('api');
export const getRoot = createSelector(
  getApiState,
  (state: ApiState) => state.root
);
export const getStack = createSelector(
  getApiState,
  (state: ApiState) => state.stack
);

import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { Action, createAction, createFeatureSelector, createReducer, createSelector, on, props } from '@ngrx/store';
import { of } from 'rxjs';
import { catchError, map, switchMap, tap } from 'rxjs/operators';

import { AuthService } from './auth.service';

export const authCheck = createAction('[Auth] Check');
export const authLogin = createAction('[Auth] Login', props<{ login: string; password: string }>());
export const authSuccess = createAction('[Auth] LoginSuccess', props<{ login: string }>());
export const authFaled = createAction('[Auth] LoginFaled', props<{ message: string }>());
export const authLogout = createAction('[Auth] Logout');

export interface AuthState {
  isValid: boolean;
  message: string;
}

const initialState: AuthState = {
  isValid: false,
  message: '',
};

const reducer = createReducer(
  initialState,
  on(authSuccess, state => ({ isValid: true, message: 'Auth is success.' })),
  on(authFaled, (state, { message }) => ({ isValid: false, message })),
  on(authLogout, state => ({ isValid: false, message: '' }))
);

export function authReducer(state: AuthState, action: Action) {
  return reducer(state, action);
}

export const getAuthState = createFeatureSelector<AuthState>('auth');
export const isAuthenticated = createSelector(
  getAuthState,
  state => state.isValid
);

@Injectable()
export class AuthEffects {
  check$ = createEffect(() =>
    this.actions$.pipe(
      ofType(authCheck),
      map(() =>
        this.authService.auth.token
          ? authSuccess({ login: this.authService.auth.login })
          : authFaled({ message: 'User is not authorized!' })
      )
    )
  );

  auth$ = createEffect(
    () =>
      this.actions$.pipe(
        ofType(authLogin),
        switchMap(a =>
          this.authService.login(a.login, a.password).pipe(
            map(() => authSuccess({ login: a.login })),
            catchError(() => of(authFaled({ message: 'Incorrect password or user.' })))
          )
        )
      ),
    { useEffectsErrorHandler: true }
  );

  logout$ = createEffect(
    () =>
      this.actions$.pipe(
        ofType(authLogout),
        tap(() => this.authService.logout())
      ),
    { dispatch: false }
  );

  constructor(private actions$: Actions, private authService: AuthService) {}
}

