  { id: 3, title: 'Hosts - Components', url: 'host_component' },
];

const [main, license, config, m_status, m_import, actions, services, hosts, host_components] = all;

const components = {
  id: 8,
  title: 'Components',
  url: 'component',
};

export const Config = {
  menu: {
    cluster: [main, config, m_status, m_import, actions, services, hosts, host_components].sort((a, b) => a.id - b.id),
    service: [main, components, config, m_status, m_import, actions],
    host: [main, config, m_status, actions],
    provider: [main, config, actions],
    bundle: [main, license],
    servicecomponent: [main, config, m_status, actions],
  },
};

@Injectable()
export class NavigationService {
  findIssue = (url: string, issue: Issue) => Object.keys(issue).some((p) => p === url || (IssueSet[url] && IssueSet[url].some((a) => a === p)));
  getIssueMessage = (flag: boolean) => (flag ? ISSUE_MESSAGE : '');

  getLeft(current: Partial<ApiBase>): INavItem[] {
    const getMenu = (c: Partial<ApiBase>) => {
      const forJob = (job: Job) => [main, ...job.log_files.map((a) => ({
        title: `${a.name} [ ${a.type} ]`,
        url: `${a.id}`,
        action: () => (location.href = a.download_url)
      }))];
      const def = (typeName: TypeName, issue: Issue, status: number) =>
        Config.menu[typeName].map((i: INavItem) => ({
          ...i,
          issue: this.findIssue(i.url, issue),
          status,
        }));
      return c.typeName === 'job' ? forJob(c as Job) : def(c.typeName, c.issue || {}, +c.status);
    };
    return getMenu(current);
  }

  getTop(current: IDetails): INavItem[] {
    const issue = (i: Issue) => (isIssue(i) ? ISSUE_MESSAGE : '');
    const link = (p: { typeName: string; id: number }) => (p ? `/${p.typeName}/${p.id}` : '');
    const typeObj = (type: string, prev: string) => ({ url: `${prev}/${type}`, title: `${type}s` });
    const fullLink = (c: { parent?: Cluster; typeName: TypeName; id: number; name: string; issue: Issue }): INavItem[] => [
      typeObj(c.typeName === 'job' ? 'task' : c.typeName, link(c.parent)),
      {
        id: c.id,
        url: `${link(c.parent)}${link(c)}`,
        title: c.name,
        issue: issue(c.issue),
      },
    ];
    return [current.parent, current].reduce((p, c) => [...p, ...(c ? fullLink(c) : [])], []);
  }
}

import { TypeName } from '@app/core/types';
import {
  Action,
  createAction,
  createFeatureSelector,
  createReducer,
  createSelector,
  on,
  props,
  select
} from '@ngrx/store';
import { pipe } from 'rxjs';
import { skip } from 'rxjs/operators';

/**
 * Event Message Object dispatched from socket
 */
export interface IEMObject {
  type: TypeName;
  id: number;
  details: {
    id?: string;
    type: string;
    value: any;
  };
}

export type EntityEvent =
  | 'add'
  | 'add_job_log'
  | 'create'
  | 'delete'
  | 'remove'
  | 'change_config'
  | 'change_state'
  | 'change_status'
  | 'change_job_status'
  | 'change_hostcomponentmap'
  | 'raise_issue'
  | 'clear_issue'
  | 'upgrade';

export interface EventMessage {
  event: EntityEvent;
  object?: IEMObject;
}

export type StatusType = 'open' | 'close' | 're-open';

export const socketInit = createAction('[Socket] Init');
export const socketOpen = createAction('[Socket] Open', props<{ status: StatusType }>());
export const socketClose = createAction('[Socket] Close', props<{ status: StatusType }>());
export const socketResponse = createAction('[Socket] Response', props<{ message: EventMessage }>());
export const clearMessages = createAction('[Socket] Clear messages');

export interface SocketState {
  status: StatusType;
  message: EventMessage;
}

const initialState: SocketState = {
  status: null,
  message: null,
};

const reducer = createReducer(
  initialState,
  on(socketInit, (state) => ({ ...state })),
  on(socketOpen, (state, { status }) => ({
    ...state,
    status,
  })),
  on(socketClose, (state, { status }) => ({
    ...state,
    status,
  })),
  on(socketResponse, (state, { message }) => ({
    ...state,
    message,
  })),
  on(clearMessages, (state) => ({ ...state, message: null }))
);

export function socketReducer(state: SocketState, action: Action) {
  return reducer(state, action);
}

export const getSocketState = createFeatureSelector<SocketState>('socket');
export const getConnectStatus = createSelector(getSocketState, (state: SocketState) => state.status);
export const getMessage = createSelector(getSocketState, (state) => state.message);
export const selectMessage = pipe(
  select(getMessage),
  skip(1),
);

