  isSelected?: boolean;
  isLink?: boolean;
  limit?: TConstraint;
  disabled: boolean;
  actions?: ActionParam[];
  color?: 'none' | 'white' | 'gray' | 'yellow';
  notification?: string;
}

export class HostTile extends Tile {
