import { Injectable } from '@angular/core';
import { IRoot } from '@app/core/types';

import { IFieldOptions, controlType, IValidator, TNBase, TNReq } from '../types';
import { getControlType, getPattern } from '../field.service';

export type TMatch = TNBase | TNReq;

/**
 *```
 {
    match: matchType;
    selector?: string;
    variants?: { [key: string]: string };
    item?: string;
    items?: IRoot;        // { [key: string]: string; }
    required_items?: string[];
    default_item?: string;
 }
 ```
 */
interface IYRoot {
  match: TMatch;
  selector?: string;
  variants?: { [key: string]: string };
  item?: string;
  items?: IRoot;
  required_items?: string[];
  default_item?: string;
}

/**
 *``` 
 {
    [key: string]: IYRoot;
 }
 ```
 */
export interface IYspec {
  [key: string]: IYRoot;
}

/**
 *```
{
    name:         string;
    path:         string[];
    type:         simpleType;
    controlType:  controlType;
    validator:    ValidatorInfo;
}
 *```
 */
export interface IYField {
  name: string;
  path: string[];
  type: TNBase;
  controlType: controlType;
  validator: IValidator;
}

/**
 * ```
 * {
 *   name:      string;
 *   type:      reqursionType;    // 'list' | 'dict'
 *   options:   IYContainer | IYField | (IYContainer | IYField)[];
 * }
 *```
 */
export interface IYContainer {
  name: string;
  type: TNReq;
  options: IYContainer | IYField | (IYContainer | IYField)[];
}

export interface IStructure extends IFieldOptions {
  rules: { options: any; type: string; name: string };
}

@Injectable()
export class YspecService {
  private root: IYspec;

  set Root(yspec: IYspec) {
    this.root = yspec;
  }

  get Root() {
    return this.root;
  }

  build(rule = 'root', path: string[] = []): IYContainer | IYField {
    const { match, item, items } = this.Root[rule];

    switch (match) {
      case 'list':
        return this.list(item, path);
      case 'dict':
        return this.dict(items, path);
      // case 'one_of':
      //   return this.one_of();
      // case 'dict_key_selection':
      //   return this.dict_key_selection();
      default:
        return this.field({ type: match, path });
    }
  }

  field(field: { type: TNBase; path: string[] }): IYField {
    const name = field.path.reverse()[0];
    return {
      name,
      type: field.type,
      path: field.path,
      controlType: getControlType(field.type),
      validator: {
        required: this.findRule(field.path, 'required_items'),
        pattern: getPattern(field.type),
      },
    };
  }

  findRule(path: string[], name: string): boolean {
    const [field, parent] = path;
    const rule = this.Root[parent];
    return !!(rule && rule[name] && Array.isArray(rule[name]) && rule[name].includes(field));
  }

  list(item: string, path: string[]): IYContainer {
    const name = [...path].reverse()[0] || 'root';
    return { type: 'list', name, options: this.build(item, [...path, item]) };
  }

  dict(items: IRoot, path: string[]): IYContainer {
    const name = [...path].reverse()[0] || 'root';
    return {
      type: 'dict',
      name,
      options: Object.keys(items).map((item_name: string) => this.build(items[item_name], [...path, item_name])),
    };
  }

  one_of() {}

  dict_key_selection() {}
}

import { Injectable } from '@angular/core';
import { ApiBase, Cluster, isIssue, Issue, Job, TypeName, IAction, LogFile, JobObject } from '@app/core/types';
import { AdcmTypedEntity } from '@app/models/entity';

export const ISSUE_MESSAGE = 'Something is wrong with your cluster configuration, please review it.';

export interface IDetails {
  parent?: Cluster;
  typeName: TypeName;
  id: number;
  name: string;
  upgradable: boolean;
  upgrade: string;
  status: string | number;
  /** link to actionss */
  action: string;
  actions: IAction[];
  issue: Issue;
  log_files?: LogFile[];
  objects: JobObject[];
  prototype_name: string;
  prototype_display_name: string;
  prototype_version: string;
  provider_id: number;
  bundle_id: number;
  hostcomponent: string;
  state: string;
}

const IssueSet: { [key: string]: string[] } = {
  service: ['required_service'],
  import: ['required_import'],
};

// type IconMenu = 'issue' | 'status';

// interface Icon {
//   id: IconMenu;
//   isShow: boolean;
//   color: ThemePalette;
//   name: string;
// }

export interface INavItem {
  id?: number;
  title: string;
  url: string;
  issue?: string;
  status?: number;
  statusMessage?: string;
  action?: () => void;
}

export interface IStyledNavItem {
  class?: string;
  entity?: AdcmTypedEntity;
}

const all = [
  { id: 0, title: 'Main', url: 'main' },
  { id: 8, title: 'License', url: 'license' },
  { id: 4, title: 'Configuration', url: 'config' },
  { id: 5, title: 'Status', url: 'status' },
  { id: 6, title: 'Import', url: 'import' },
  { id: 7, title: 'Actions', url: 'action' },
  { id: 1, title: 'Services', url: 'service' },
  { id: 2, title: 'Hosts', url: 'host' },
  { id: 3, title: 'Hosts - Components', url: 'host_component' },
];

const [main, license, config, m_status, m_import, actions, services, hosts, host_components] = all;

const components = {
  id: 8,
  title: 'Components',
  url: 'component',
};

export const Config = {
  menu: {
    cluster: [main, config, m_status, m_import, actions, services, hosts, host_components].sort((a, b) => a.id - b.id),
    service: [main, components, config, m_status, m_import, actions],
    host: [main, config, m_status, actions],
    provider: [main, config, actions],
    bundle: [main, license],
    servicecomponent: [main, config, m_status, actions],
  },
};

@Injectable()
export class NavigationService {
  findIssue = (url: string, issue: Issue) => Object.keys(issue).some((p) => p === url || (IssueSet[url] && IssueSet[url].some((a) => a === p)));
  getIssueMessage = (flag: boolean) => (flag ? ISSUE_MESSAGE : '');

  getLeft(current: Partial<ApiBase>): INavItem[] {
    const getMenu = (c: Partial<ApiBase>) => {
      const forJob = (job: Job) => [main, ...job.log_files.map((a) => ({
        title: `${a.name} [ ${a.type} ]`,
        url: `${a.id}`,
        action: () => (location.href = a.download_url)
      }))];
      const def = (typeName: TypeName, issue: Issue, status: number) =>
        Config.menu[typeName].map((i: INavItem) => ({
          ...i,
          issue: this.findIssue(i.url, issue),
          status,
        }));
      return c.typeName === 'job' ? forJob(c as Job) : def(c.typeName, c.issue || {}, +c.status);
    };
    return getMenu(current);
  }

  getTop(current: IDetails): INavItem[] {
    const issue = (i: Issue) => (isIssue(i) ? ISSUE_MESSAGE : '');
    const link = (p: { typeName: string; id: number }) => (p ? `/${p.typeName}/${p.id}` : '');
    const typeObj = (type: string, prev: string) => ({ url: `${prev}/${type}`, title: `${type}s` });
    const fullLink = (c: { parent?: Cluster; typeName: TypeName; id: number; name: string; issue: Issue }): INavItem[] => [
      typeObj(c.typeName === 'job' ? 'task' : c.typeName, link(c.parent)),
      {
        id: c.id,
        url: `${link(c.parent)}${link(c)}`,
        title: c.name,
        issue: issue(c.issue),
      },
    ];
    return [current.parent, current].reduce((p, c) => [...p, ...(c ? fullLink(c) : [])], []);
  }
}

import { Component, OnInit, ViewChild } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Store } from '@ngrx/store';
import { Subject } from 'rxjs';

import { ClusterService } from '@app/core/services/cluster.service';
import { EventMessage, SocketState } from '@app/core/store';
import { Job, JobStatus, LogFile } from '@app/core/types';
import { SocketListenerDirective } from '@app/shared/directives';

import { TextComponent } from './text.component';

export interface ITimeInfo {
  start: string;
  end: string;
  time: string;
}

@Component({
  selector: 'app-job-log',
  styles: [
    `
      :host {
        display: flex;
        flex: 1;
        padding: 10px 20px;
      }

      div.wrap {
        display: flex;
        flex: 1;
      }

      .accordion {
        flex: 1;
        display: flex;
        flex-direction: column;
      }
    `,
  ],
  template: `
    <app-job-info [timeInfo]="timeInfo" [status]="job.status"></app-job-info>
    <div class="wrap" *ngIf="currentLog$ | async as log">
      <app-log-text *ngIf="log.type !== 'check'" [content]="log.content" [status]="job.status" (refresh)="refresh()"></app-log-text>
      <mat-accordion *ngIf="log.type === 'check'" class="accordion">
        <app-log-check [content]="log.content"></app-log-check>
      </mat-accordion>
    </div>
  `,
})
export class LogComponent extends SocketListenerDirective implements OnInit {
  currentLog$ = new Subject<LogFile>();
  timeInfo: ITimeInfo;
  logUrl: string;

  @ViewChild(TextComponent, { static: true }) textComp: TextComponent;

  constructor(private service: ClusterService, private route: ActivatedRoute, public store: Store<SocketState>) {
    super(store);
  }

  get job(): Job {
    return this.service.Current as Job;
  }

  ngOnInit() {
    this.timeInfo = this.service.getOperationTimeData(this.job);
    this.route.paramMap.pipe(this.takeUntil()).subscribe((p) => {
      this.logUrl = this.job.log_files.find((a) => a.id === +p.get('log')).url;
      this.refresh();
    });
    this.startListenSocket();
  }

  socketListener(m: EventMessage) {
    if (m?.object?.type === 'job' && m?.object?.id === this.job.id) {
      if (m.event === 'change_job_status') {
        const job = this.job;
        job.status = m.object.details.value as JobStatus;
        job.finish_date = new Date().toISOString();
        this.timeInfo = this.service.getOperationTimeData(job);
        if (this.textComp) this.textComp.update(job.status);
      }
      this.refresh();
    }
  }

  refresh() {
    if (!this.logUrl) return;
    this.service.getLog(this.logUrl).subscribe((a) => this.currentLog$.next(a));
  }
}

import { Injectable } from '@angular/core';
import { AbstractControl, FormBuilder, FormGroup, ValidatorFn, Validators } from '@angular/forms';
import { isEmptyObject } from '@app/core/types';

import { ISearchParam } from './main/main.service';
import { controlType, IConfig, IConfigAttr, IFieldOptions, IFieldStack, ILimits, IPanelOptions, IValidator, resultTypes, TNBase, TNForm } from './types';

export type TFormOptions = IFieldOptions | IPanelOptions;

export interface IOutput {
  [key: string]: resultTypes;
}

export interface ISource {
  name: string;
  subname: string;
  type: TNForm;
  read_only: boolean;
  limits?: ILimits;
  value: any;
}

export interface IToolsEvent {
  name: string;
  conditions?: { advanced: boolean; search: string } | boolean;
}

const isVisibleField = (a: TFormOptions) => !a.ui_options?.invisible;
const isAdvancedField = (a: TFormOptions) => isVisibleField(a) && a.ui_options?.advanced;
const isHidden = (a: IFieldStack) => !!(a.ui_options?.invisible || a.ui_options?.advanced);

const typeToControl: Partial<{ [key in TNForm | controlType]: controlType }> = {
  bool: 'boolean',
  int: 'textbox',
  integer: 'textbox',
  float: 'textbox',
  string: 'textbox',
  file: 'textarea',
  text: 'textarea',
};

export const getControlType = (t: TNForm): controlType => typeToControl[t] || (t as controlType);

const intPattern = () => new RegExp(/^[-]?\d+$/);
const patternFn = {
  integer: intPattern,
  int: intPattern,
  float: () => new RegExp(/^[-]?[0-9]+(\.[0-9]+)?$/),
};

export const getPattern = (t: TNForm): RegExp => (patternFn[t] ? patternFn[t]() : null);

const fn = {
  boolean: (v: boolean | null, d: boolean | null, r: boolean): boolean | null => (String(v) === 'true' || String(v) === 'false' || String(v) === 'null' ? v : r ? d : null),
  json: (v: string): string => (v === null ? '' : JSON.stringify(v, undefined, 4)),
  map: (v: object, d: object): object => (!v ? d : v),
  list: (v: string[], d: string[]): string[] => (!v ? d : v),
  structure: (v: any): any => v,
};

export const getValue = (t: TNForm) => {
  const def = (value: number | string) => (value === null || value === undefined ? '' : String(value));
  return fn[t] ? fn[t] : def;
};

export const getKey = (name: string, subname: string): string => (subname ? `${subname}/${name}` : name);

export const getValidator = (required: boolean, min: number, max: number, type: TNForm) => ({
  required,
  min,
  max,
  pattern: getPattern(type),
});

const getField = (item: IFieldStack): IFieldOptions => ({
  ...item,
  key: getKey(item.name, item.subname),
  value: getValue(item.type)(item.value, item.default, item.required),
  validator: getValidator(item.required, item.limits?.min, item.limits?.max, item.type),
  controlType: getControlType(item.type),
  hidden: item.name === '__main_info' || isHidden(item),
  compare: [],
});

const fo = (n: string) => (b: IFieldStack) => b.type !== 'group' && b.subname && b.name === n;
const isActive = (a: IConfigAttr, n: string) => a[n]?.active;
export const getOptions = (a: IFieldStack, d: IConfig) =>
  d.config
    .filter(fo(a.name))
    .map(getField)
    // switch off validation for field if !(activatable: true && active: false) - line: 146
    .map((c) => ({ ...c, name: c.subname, activatable: a.activatable && !isActive(d.attr, a.name) }));

const getPanel = (a: IFieldStack, d: IConfig): IPanelOptions => ({
  ...a,
  hidden: isHidden(a),
  active: a.activatable ? isActive(d.attr, a.name) : true,
  options: getOptions(a, d),
});

const handleTree = (c: ISearchParam): ((a: TFormOptions) => TFormOptions) => (a: TFormOptions): TFormOptions => {
  if ('options' in a) {
    a.options = a.options.map(handleTree(c));
    if (c.search) a.hidden = a.options.filter((b) => !b.hidden).length === 0;
    else a.hidden = isAdvancedField(a) ? !c.advanced : false;
  } else if (isVisibleField(a)) {
    a.hidden = !(a.display_name.toLowerCase().includes(c.search.toLowerCase()) || String(a.value).toLocaleLowerCase().includes(c.search.toLocaleLowerCase()));
    if (!a.hidden && isAdvancedField(a)) a.hidden = !c.advanced;
  }
  return a;
};

@Injectable()
export class FieldService {
  constructor(public fb: FormBuilder) {}

  /**
   * Parse and prepare source data from backend
   */
  public getPanels(data: IConfig): TFormOptions[] {
    return data?.config
      ?.filter((a) => a.name !== '__main_info')
      .reduce((p, c) => {
        if (c.subname) return p;
        if (c.type !== 'group') return [...p, getField(c)];
        else return [...p, getPanel(c, data)];
      }, []);
  }

  /**
   * Generate FormGroup
   * @param options
   */
  public toFormGroup(options: TFormOptions[] = []): FormGroup {
    const check = (a: TFormOptions): boolean =>
      'options' in a
        ? a.activatable
          ? isVisibleField(a) // if group.activatable - only visible
          : isVisibleField(a) && !a.read_only // else visible an not read_only
          ? a.options.some((b) => check(b)) // check inner fields
          : false
        : isVisibleField(a) && !a.read_only; // for fields in group

    return this.fb.group(
      options.reduce((p, c) => this.runByTree(c, p), {}),
      {
        validator: () => (options.filter(check).length === 0 ? { error: 'Form is empty' } : null),
      }
    );
  }

  // TODO:
  private runByTree(field: TFormOptions, controls: { [key: string]: {} }): { [key: string]: {} } {
    if ('options' in field) {
      controls[field.name] = this.fb.group(
        field.options.reduce((p, a) => {
          if ('options' in a) this.fb.group(this.runByTree(a, p));
          else this.fillForm(a, p);
          return p;
        }, {})
      );
      return controls;
    } else {
      return this.fillForm(field, controls);
    }
  }

  private fillForm(field: IFieldOptions, controls: {}) {
    const name = field.subname || field.name;
    controls[name] = this.fb.control(field.value, field.activatable ? [] : this.setValidator(field));
    return controls;
  }

  /**
   * External use (scheme.service) to set validator for FormControl by type
   * @param field Partial<FieldOptions>{ ValidatorInfo, controlType }
   */
  public setValidator(field: { validator: IValidator; controlType: controlType }, controlToCompare?: AbstractControl) {
    const v: ValidatorFn[] = [];

    if (field.validator.required) v.push(Validators.required);
    if (field.validator.pattern) v.push(Validators.pattern(field.validator.pattern));
    //if (field.validator.max !== null)
    v.push(Validators.max(field.validator.max));
    //if (field.validator.min !== null)
    v.push(Validators.min(field.validator.min));

    if (field.controlType === 'password') {
      const passwordConfirm = (): ValidatorFn => (control: AbstractControl): { [key: string]: any } | null => {
        if (controlToCompare && controlToCompare.value !== control.value) return { notEqual: true };
        return null;
      };
      v.push(passwordConfirm());
    }

    if (field.controlType === 'json') {
      const jsonParse = (): ValidatorFn => (control: AbstractControl): { [key: string]: any } | null => {
        if (control.value) {
          try {
            JSON.parse(control.value);
            return null;
          } catch (e) {
            return { jsonParseError: { value: control.value } };
          }
        } else return null;
      };

      v.push(jsonParse());
    }

    if (field.controlType === 'map') {
      const parseKey = (): ValidatorFn => (control: AbstractControl): { [key: string]: any } | null =>
        control.value && Object.keys(control.value).length && Object.keys(control.value).some((a) => !a) ? { parseKey: true } : null;
      v.push(parseKey());
    }
    return v;
  }

  /**
   * Filter by group and all fields
   */
  public filterApply(options: TFormOptions[], c: ISearchParam): TFormOptions[] {
    return options.filter((a) => isVisibleField(a)).map(handleTree(c));
  }

  /**
   * Output form, cast to source type
   */
  public parseValue(output: IOutput, source: ISource[]): IOutput {
    const findField = (name: string, p?: string): Partial<IFieldStack> => source.find((a) => (p ? a.name === p && a.subname === name : a.name === name) && !a.read_only);

    const runYspecParse = (v: any, f: Partial<IFieldOptions>) => ((!v || !Object.keys(v).length) && !f.value ? f.value : this.runYspec(v, f.limits.rules));

    const runParse = (v: IOutput, parentName?: string): IOutput => {
      const runByValue = (p: IOutput, c: string) => {
        const checkType = (data: resultTypes | IOutput, field: Partial<IFieldStack>): resultTypes => {
          const { type } = field;
          if (type === 'structure') return runYspecParse(data, field);
          else if (type === 'group') return this.checkValue(runParse(data as IOutput, field.name), type);
          else return this.checkValue(data, type);
        };

        const f = findField(c, parentName);
        if (f) {
          const result = checkType(v[c], f);
          return f.type !== 'group' || result ? { ...p, [c]: result } : p;
        }
        return p;
      };

      return Object.keys(v).reduce(runByValue, {});
    };

    const __main_info = findField('__main_info');
    return runParse(__main_info?.required ? { ...output, __main_info: __main_info.value } : { ...output });
  }

  private runYspec(value: resultTypes, rules: any) {
    switch (rules?.type) {
      case 'list': {
        return (value as Array<TNBase>).filter((a) => !!a).map((a) => this.runYspec(a, rules.options));
      }
      case 'dict': {
        return Object.keys(value).reduce((p, c) => {
          const r = rules.options.find((b: any) => b.name === c);
          const v = r ? this.runYspec(value[c], r) : null;
          return v !== null ? { ...p, [c]: v } : { ...p };
        }, {});
      }
      default: {
        return this.checkValue(value, rules?.type);
      }
    }
  }

  checkValue(value: resultTypes, type: TNForm): resultTypes {
    if (value === '' || value === null || isEmptyObject(value)) return null;
    if (typeof value === 'boolean') return value;
    else if (typeof value === 'string')
      switch (type) {
        case 'option':
          return !isNaN(+value) ? parseInt(value, 10) : value;
        case 'integer':
        case 'int':
          return parseInt(value, 10);
        case 'float':
          return parseFloat(value);
        case 'json':
          return JSON.parse(value);
      }
    else
      switch (type) {
        case 'map':
          return Object.keys(value)
            .filter((a) => !!a)
            .reduce((p, c) => ({ ...p, [c]: value[c] }), {});

        case 'list':
          return Array.isArray(value) ? (value as Array<string>).filter((a) => !!a) : null;
      }

    return value;
  }
}

import { IConfig } from '@app/shared/configuration/types';

/**
 * 
 *```
{
    action: 'add' | 'remove';  
    component: string;    // name of servise to work with  
    service: string;      // name of component to work with
}
```
 *
 */
export interface IActionParameter {
  action: 'add' | 'remove';
  component: string;
  service: string;
}

export interface IUIOptions {
  disclaimer?: string;
}

export interface IAction {
  name: string;
  description: string;
  display_name: string;
  run: string;
  config: IConfig;
  hostcomponentmap: IActionParameter[];
  button: 'create_host' | null;
  ui_options: IUIOptions;
  children?: IAction[];
}

import { IYspec } from './yspec/yspec.service';
import { TFormOptions } from './field.service';

export type stateType = 'created' | 'locked';

export type TNBase = 'string' | 'integer' | 'int' | 'boolean' | 'bool' | 'float';
export type TNReq = 'dict' | 'list';
export type TNSpec = 'structure' | 'group' | 'option' | 'json' | 'map' | 'file' | 'text' | 'password' | 'variant';
export type TNForm = TNBase | TNReq | TNSpec;
export type simpleTypes = string | number | boolean;
export type resultTypes = simpleTypes | simpleTypes[] | object;
export type TValue = string | number | boolean | object | any[];

/**
 *```
 {
    invisible?: boolean;
    no_confirm?: boolean;
    advanced?: boolean;
 }
 ```
 *
 */
export interface IUIoptions {
  invisible?: boolean;
  no_confirm?: boolean;
  advanced?: boolean;
}

/**
 * ```
 {
    min?: number;
    max?: number;
    option?: any;
    read_only?: stateType[];   // created | locked
    yspec?: IYspec;
    rules?: any;
    active?: boolean;
}
 * ```
 */
export interface IVariantSet {
  name?: string;
  strict: boolean;
  type: 'config' | 'inline';
  value: string[];
}

export interface ILimits {
  min?: number;
  max?: number;
  option?: any;
  read_only?: stateType[];
  yspec?: IYspec;
  rules?: any;
  active?: boolean;
  source?: IVariantSet;
