import { Injectable } from '@angular/core';
import { IRoot } from '@app/core/types/api';
import { environment } from '@env/environment';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { Store } from '@ngrx/store';
import { of } from 'rxjs';
import { catchError, delay, exhaustMap, filter, map, switchMap, withLatestFrom } from 'rxjs/operators';

import { State } from '../store';
import { loadRoot, loadStack, rootError, rootSuccess, stackSuccess } from './api.reducer';
import { ApiService } from './api.service';

@Injectable()
export class ApiEffects {
  root$ = createEffect(() =>
    this.actions$.pipe(
      ofType(loadRoot),
      exhaustMap(() =>
        this.api.get<IRoot>(environment.apiRoot).pipe(
          map(root => rootSuccess({ root })),
          catchError(() => of(rootError()))
        )
      )
    )
  );

  retry$ = createEffect(() =>
    this.actions$.pipe(
      ofType(rootError),
      delay(3000),
      switchMap(() =>
        this.api.get<IRoot>(environment.apiRoot).pipe(
          map(root => rootSuccess({ root })),
          catchError(() => of(rootError()))
        )
      )
    )
  );

  stack$ = createEffect(() =>
    this.actions$.pipe(
      ofType(loadStack),
      withLatestFrom(this.store, (actions, store) => store.api),
      filter(api => api.root && !api.stack),
      exhaustMap(api => this.api.get<IRoot>(api.root.stack).pipe(map(stack => stackSuccess({ stack }))))
    )
  );

  constructor(private actions$: Actions, private api: ApiService, private store: Store<State>) {}
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { ParamMap } from '@angular/router';
import { IRoot, TypeName } from '@app/core/types/api';
import { ListResult } from '@app/models/list-result';
import { select, Store } from '@ngrx/store';
import { EMPTY, Observable } from 'rxjs';
import { catchError, filter, switchMap } from 'rxjs/operators';

import { State } from '../store';
import { getRoot } from './api.reducer';

@Injectable()
export class ApiService {
  constructor(private http: HttpClient, private store: Store<State>) {}

  get root(): Observable<IRoot> {
    return this.store.pipe(select(getRoot)).pipe(filter((root) => !!root));
  }

  getPure<T>(typeName: TypeName, params: { [key: string]: string } = {}): Observable<T[]> {
    return this.root.pipe(switchMap((root) => this.get<T[]>(root[typeName], params))).pipe(catchError(() => EMPTY));
  }

  getOne<T>(typeName: TypeName, id: number, params: { [key: string]: string } = {}) {
    return this.root.pipe(switchMap((root) => this.get<T>(`${root[typeName]}${id}/`, params))).pipe(catchError(() => EMPTY));
  }

  get<T>(url: string, params: { [key: string]: string } = {}): Observable<T> {
    return this.http.get<T>(url, { params });
  }

  getList<T>(url: string, p: ParamMap): Observable<ListResult<T>> {
    const params = p.keys.reduce((pr, c) => ({ ...pr, [c]: p.get(c) }), {});
    if (p) {
      const limit = p.get('limit') ? +p.get('limit') : +localStorage.getItem('limit'),
        offset = (p.get('page') ? +p.get('page') : 0) * limit;
      params['limit'] = limit.toString();
      params['offset'] = offset.toString();
      params['status'] = p.get('filter') || '';
    }
    return this.get<ListResult<T>>(url, params);
  }

  list<T>(url: string, params: { limit: string; offset: string; ordering?: string } | null) {
    if (!params) {
      params = { limit: localStorage.getItem('limit'), offset: '0' };
    }
    return this.get<ListResult<T>>(url, params);
  }

  post<T>(url: string, item: Object | FormData): Observable<T> {
    return this.http.post<T>(url, item);
  }

  put<T>(url: string, item: any): Observable<T> {
    return this.http.put<T>(url, item).pipe(catchError(() => EMPTY));
  }

  patch<T>(url: string, item: any): Observable<T> {
    return this.http.patch<T>(url, item).pipe(catchError(() => EMPTY));
  }

  delete(url: string) {
    return this.http.delete(url).pipe(catchError(() => EMPTY));
  }
}

import { Injectable } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { NavigationStart, Router } from '@angular/router';
import { getConnectStatus, getFirstAdminLogin, getProfile, getRoot, isAuthenticated, loadProfile, loadRoot, loadStack, rootError, socketInit, State } from '@app/core/store';
import { select, Store } from '@ngrx/store';
import { combineLatest } from 'rxjs';
import { filter, switchMap, tap } from 'rxjs/operators';

import { ChannelService, keyChannelStrim } from '../channel.service';
import { ConfigService, IVersionInfo } from '../config.service';

@Injectable()
export class AppService {
  constructor(
    private store: Store<State>,
    private config: ConfigService,
    private router: Router,
    private dialog: MatDialog,
    private channel: ChannelService,
    public snackBar: MatSnackBar
  ) {}

  getRootAndCheckAuth() {
    this.store.dispatch(loadRoot());
    const b$ = this.store.pipe(select(getRoot));
    const a$ = this.store.pipe(select(isAuthenticated));
    return combineLatest([a$, b$]).pipe(
      filter((a) => a[0] && !!a[1]),
      switchMap((_) => this.config.load()),
      tap((c) => {
        if (!c) {
          this.channel.next(keyChannelStrim.notifying, 'New version available. Page has been refreshed.');
          setTimeout(() => location.reload(), 2000);
        } else {
          this.store.dispatch(socketInit());
          this.store.dispatch(loadStack());
          this.store.dispatch(loadProfile());
        }
      })
    );
  }

  checkWSconnectStatus() {
    return this.store.pipe(
      select(getConnectStatus),
      filter((a) => !!a),
      tap((status) => {
        if (status === 'open') this.channel.next(keyChannelStrim.notifying, 'Connection established.');
        if (status === 'close') {
          this.channel.next(keyChannelStrim.notifying, 'Connection lost. Recovery attempt.::error');
          this.store.dispatch(rootError());
        }
      })
    );
  }

  checkUserProfile() {
    return this.store.pipe(
      select(getProfile),
      filter((u) => u.settingsSaved)
    );
  }

  getVersion(versionData: IVersionInfo): IVersionInfo {
    return this.config.version.split('-').reduce((p, c, i) => ({ ...p, [Object.keys(versionData)[i]]: c }), {} as IVersionInfo);
  }

  initListeners() {
    // check user profile settings - this is the first entry
    this.store
      .pipe(
        select(getFirstAdminLogin),
        filter((u) => u)
      )
      .subscribe(() => this.router.navigate(['admin']));

    // close dialog
    this.router.events.pipe(filter((e) => e instanceof NavigationStart)).subscribe(() => this.dialog.closeAll());

    // notification
    this.channel.on<string>(keyChannelStrim.notifying).subscribe((m) => {
      const astr = m.split('::');
      const data = astr[1]
        ? { panelClass: 'snack-bar-error' }
        : {
            duration: 5000,
            panelClass: 'snack-bar-notify',
          };
      this.snackBar.open(astr[0], 'Hide', data);
    });

    // test only
    // this.store
    //   .select(getMessage)
    //   .pipe(filter((e) => !!e))
    //   .subscribe((e) => console.log('EVENT:', e.event, { ...e.object, details: JSON.stringify(e.object.details) }));
  }
}

