import { Injectable, NgZone } from '@angular/core';
import { NavigationStart, Router } from '@angular/router';
import { interval } from 'rxjs';
import { distinctUntilChanged, filter, map, startWith, take, tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root',
})
export class FullyRenderedService {
  navStart$ = this.router.events
    .pipe(
      filter((event) => event instanceof NavigationStart),
      startWith(null as string), // Start with something, because the app doesn't fire this on appload, only on subsequent route changes
      tap((event) => {
        /* Place code to track NavigationStart here */
      })
    )
    .subscribe();

  constructor(private router: Router, private zone: NgZone) {}

  /**
   *
   *
   * @param {() => void} callback
   * @memberof FullyRenderedService
   */
  stableView(callback: () => void) {
    this.zone.runOutsideAngular(() => {
      interval(10)
        .pipe(
          startWith(0),
          // To prevent a memory leak on two closely times route changes, take until the next nav start
          //takeUntil(this.navigationStart$),
          // Turn the interval number into the current state of the zone
          map(() => !this.zone.hasPendingMacrotasks),
          // Don't emit until the zone state actually flips from `false` to `true`
          distinctUntilChanged(),
          // Filter out unstable event. Only emit once the state is stable again
          filter((stateStable) => stateStable === true),
          // Complete the observable after it emits the first result
          take(1),
          tap((stateStable) => {
            //console.log('FULLY RENDERED!!!!');
            callback();
          })
        )
        .subscribe();
    });
  }
}

import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { Action, createAction, createFeatureSelector, createReducer, createSelector, on, props, Store } from '@ngrx/store';
import { exhaustMap, filter, map, withLatestFrom } from 'rxjs/operators';

import { ApiService } from '../api';
import { State } from '../store';
import { ApiBase, Issue } from '../types';

export interface IssueState {
  value: Issue;
  url: string;
}

const InitState = {
  value: null,
  url: '',
};

export const loadIssue = createAction('[Issue] LoadIssue');
export const fillIssue = createAction('[Issue] FillIssue', props<{ value: Issue; url: string }>());

const reducer = createReducer(InitState, on(loadIssue, state => ({ ...state })), on(fillIssue, (state, { value, url }) => ({ value, url })));

export function issueReducer(state: IssueState, action: Action) {
  return reducer(state, action);
}

@Injectable()
export class IssueEffect {
  load$ = createEffect(() =>
    this.actions$.pipe(
      ofType(loadIssue),
      withLatestFrom(this.store, (action, state) => state.issue.url),
      filter(url => !!url),
      exhaustMap(url => this.api.get<ApiBase>(url).pipe(map(o => fillIssue({ value: o.issue, url: o.url }))))
    )
  );

  constructor(private actions$: Actions, private api: ApiService, private store: Store<State>) {}
}

export const getIssueSelector = createFeatureSelector<IssueState>('issue');

export const checkIssue = createSelector(
  getIssueSelector,
  state => state
);

import { Injectable } from '@angular/core';

@Injectable()
export class IssueMessageService {

  constructor() { }

  parse(issueMessage: string): string[] {
    let result = [];
    for (const item of issueMessage.matchAll(/(.*?)(\$\{.+?\})|(.+$)/g)) {
      if (item.length) {
        result = [ ...result, ...item.slice(1, item.length) ];
      }
    }

    return result.filter(item => !!item);
  }

}

import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { filter, tap } from 'rxjs/operators';
import { Store } from '@ngrx/store';

import { EventableService } from '@app/models/eventable-service';
import { EntityEvent, EventMessage, selectMessage, SocketState } from '@app/core/store';

@Injectable()
export class JobService implements EventableService {

  constructor(
    private store: Store<SocketState>,
  ) {}

  events(events?: EntityEvent[]): Observable<EventMessage> {
    return this.store.pipe(
      selectMessage,
      filter(event => event?.object?.type === 'job'),
      filter(event => !events || events.includes(event?.event)),
    );
  }

}

import { Injectable } from '@angular/core';
import { ParamMap, Params, convertToParamMap } from '@angular/router';
import { switchMap, tap, map } from 'rxjs/operators';
import { Observable } from 'rxjs';

import { environment } from '@env/environment';
import { ApiService } from '@app/core/api';
import { ClusterService } from '@app/core/services/cluster.service';
import { Bundle, Cluster, Entities, Host, IAction, Service, TypeName } from '@app/core/types';

const COLUMNS_SET = {
  cluster: ['name', 'prototype_version', 'description', 'state', 'status', 'actions', 'import', 'upgrade', 'config', 'controls'],
  host2cluster: ['fqdn', 'provider_name', 'state', 'status', 'actions', 'config', 'remove'],
  service2cluster: ['display_name', 'version_no_sort', 'state', 'status', 'actions', 'import', 'config'],
  host: ['fqdn', 'provider_name', 'host2cluster', 'state', 'status', 'actions', 'config', 'controls'],
  provider: ['name', 'prototype_version', 'state', 'actions', 'upgrade', 'config', 'controls'],
  job: ['action', 'objects', 'start_date', 'finish_date', 'status'],
  task: ['id', 'start_date', 'finish_date', 'status'],
  bundle: ['name', 'version', 'edition', 'description', 'controls'],
};


export interface ListInstance {
  typeName: TypeName;
  columns: string[];
}

@Injectable({
  providedIn: 'root',
})
export class ListService {
  current: ListInstance;

  constructor(private api: ApiService, private detail: ClusterService) {}

  initInstance(typeName: TypeName): ListInstance {
    this.current = { typeName, columns: COLUMNS_SET[typeName] };
    return this.current;
  }

  getList(p: ParamMap, typeName: string) {
    const listParamStr = localStorage.getItem('list:param');
    if (p?.keys.length) {
      const param = p.keys.reduce((a, c) => ({ ...a, [c]: p.get(c) }), {});
      if (listParamStr) {
        const json = JSON.parse(listParamStr);
        json[typeName] = param;
        localStorage.setItem('list:param', JSON.stringify(json));
      } else localStorage.setItem('list:param', JSON.stringify({ [typeName]: param }));
    }

    switch (typeName) {
      case 'host2cluster':
        return this.detail.getHosts(p);
      case 'service2cluster':
        return this.detail.getServices(p);
      case 'bundle':
        return this.api.getList<Bundle>(`${environment.apiRoot}stack/bundle/`, p);
      case 'servicecomponent':
        return this.api.getList(`${environment.apiRoot}cluster/${(this.detail.Current as Service).cluster_id}/service/${this.detail.Current.id}/component`, p);
      default:
        return this.api.root.pipe(switchMap((root) => this.api.getList<Entities>(root[this.current.typeName], p)));
    }
  }

  getCrumbs() {
    return [{ path: '/cluster', name: 'clusters' }];
  }

  getActions(row: Entities) {
    this.api
      .get<IAction[]>(row.action)
      .pipe(tap((actions) => (row.actions = actions)))
      .subscribe();
  }

  delete(row: Entities) {
    return this.api.delete(row.url);
  }

  // host
  getClustersForHost(param: Params): Observable<{ id: number; title: string }[]> {
    return this.api.root
      .pipe(switchMap((root) => this.api.getList<Cluster>(root.cluster, convertToParamMap(param))))
      .pipe(map((res) => res.results.map((a) => ({ id: a.id, title: a.name }))));
  }

  addClusterToHost(cluster_id: number, row: Host): Observable<Host> {
    return this.api
      .post<Host>(`${environment.apiRoot}cluster/${cluster_id}/host/`, { host_id: row.id })
      .pipe(tap((host) => {
        row.cluster_id = host.cluster_id;
        row.cluster_name = host.cluster;
      }));
  }

  checkItem<T>(item: Entities) {
    return this.api.get<T>(item.url);
  }

  acceptLicense(url: string) {
    return this.api.put(url, {});
  }

  getLicenseInfo(url: string) {
    return this.api.get<{ text: string }>(url);
  }
}

import { animate, state, style, transition, trigger } from '@angular/animations';
import { Injectable } from '@angular/core';
