    state('show', style({ top: '134px' })),
    state('hideTools', style({ opacity: 0 })),
    state('showTools', style({ opacity: 0.8 })),
    transition('hideTools => showTools', animate('.5s .3s ease-in')),
    transition('showTools => hideTools', animate('.2s ease-out')),
