import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { concatMap, filter, map, switchMap, take } from 'rxjs/operators';
import { Observable } from 'rxjs';
import { AdcmEntity, AdcmTypedEntity } from '@app/models/entity';
import { TypeName } from '@app/core/types';

import { Action, Store } from '@ngrx/store';
import { ApiService } from '@app/core/api';
import { ServiceComponentService } from '@app/services/service-component.service';
import { ClusterService } from '@app/core/services/cluster.service';
import { EntityNames } from '@app/models/entity-names';
import {
  getEventEntityType,
  getNavigationPath,
  getPath,
  setPath,
  setPathOfRoute
} from '@app/store/navigation/navigation.store';
import { EventMessage, socketResponse } from '@app/core/store/sockets/socket.reducer';
import { IClusterService } from '@app/models/cluster-service';

@Injectable()
export class NavigationEffects {

  setPathOfRoute$ = createEffect(
    () =>
      this.actions$.pipe(
        ofType(setPathOfRoute),
        filter(action => !!action.params),
        switchMap(action => {
          const getters: Observable<AdcmTypedEntity>[] = action.params.keys.reduce((acc, param) => {
            const getter = this.entityGetter(param as TypeName, +action.params.get(param));
            if (getter) {
              acc.push(getter);
            }

            return acc;
          }, []);
          return getPath(getters);
        }),
      ),
  );

  changePathOfEvent$ = createEffect(() => this.actions$.pipe(
    ofType(socketResponse),
    filter(action => ['raise_issue', 'clear_issue'].includes(action.message.event)),
    concatMap((event: { message: EventMessage }) => {
      return new Observable<Action>(subscriber => {
        this.store.select(getNavigationPath).pipe(take(1)).subscribe((path) => {
          if (path.some(item => item.typeName === getEventEntityType(event.message.object.type) && event.message.object.id === item.id)) {
            this.entityGetter(getEventEntityType(event.message.object.type), event.message.object.id)
              .subscribe((entity) => {
                subscriber.next(setPath({
                  path: path.reduce((acc, item) =>
                    acc.concat(getEventEntityType(event.message.object.type) === item.typeName && item.id === event.message.object.id ? entity : item), []),
                }));
                subscriber.complete();
              }, () => subscriber.complete());
          } else {
            subscriber.complete();
          }
        }, () => subscriber.complete());
      });
    }),
  ));

  constructor(
    private actions$: Actions,
    private api: ApiService,
    private serviceComponentService: ServiceComponentService,
    private store: Store,
    private clusterService: ClusterService
  ) {}

  entityGetter(type: TypeName, id: number): Observable<AdcmTypedEntity> {
    const entityToTypedEntity = (getter: Observable<AdcmEntity>, typeName: TypeName) => getter.pipe(
      map(entity => ({
        ...entity,
        typeName,
      } as AdcmTypedEntity))
    );


    if (EntityNames.includes(type)) {
      if (type === 'bundle') {
        return entityToTypedEntity(
          this.clusterService.one_bundle(id),
          type,
        );
      } else if (type === 'servicecomponent') {
        return entityToTypedEntity(
          this.serviceComponentService.get(id),
          type,
        );
      } if (type === 'service') {
        return entityToTypedEntity(
          this.api.getOne<any>(type, id),
          type,
        ).pipe(switchMap((entity) => {
          return this.api.getOne<any>('cluster', (entity as any as IClusterService).cluster_id)
            .pipe(map(cluster => ({...entity, cluster})));
        }));
      } else {
        return entityToTypedEntity(
          this.api.getOne<any>(type, id),
          type,
        );
      }
    }
  }

}

import { Injectable } from '@angular/core';
import { ApiBase, Cluster, isIssue, Issue, Job, TypeName, IAction, LogFile, JobObject } from '@app/core/types';
import { AdcmTypedEntity } from '@app/models/entity';

export const ISSUE_MESSAGE = 'Something is wrong with your cluster configuration, please review it.';

export interface IDetails {
  parent?: Cluster;
  typeName: TypeName;
  id: number;
  name: string;
  upgradable: boolean;
  upgrade: string;
  status: string | number;
  /** link to actionss */
  action: string;
  actions: IAction[];
  issue: Issue;
  log_files?: LogFile[];
  objects: JobObject[];
  prototype_name: string;
  prototype_display_name: string;
  prototype_version: string;
  provider_id: number;
  bundle_id: number;
  hostcomponent: string;
  state: string;
}

const IssueSet: { [key: string]: string[] } = {
  service: ['required_service'],
  import: ['required_import'],
};

// type IconMenu = 'issue' | 'status';

// interface Icon {
//   id: IconMenu;
//   isShow: boolean;
//   color: ThemePalette;
//   name: string;
// }

export interface INavItem {
  id?: number;
  title: string;
  url: string;
  issue?: string;
  status?: number;
  statusMessage?: string;
  action?: () => void;
}

export interface IStyledNavItem {
  class?: string;
  entity?: AdcmTypedEntity;
}

const all = [
  { id: 0, title: 'Main', url: 'main' },
  { id: 8, title: 'License', url: 'license' },
  { id: 4, title: 'Configuration', url: 'config' },
  { id: 5, title: 'Status', url: 'status' },
  { id: 6, title: 'Import', url: 'import' },
  { id: 7, title: 'Actions', url: 'action' },
  { id: 1, title: 'Services', url: 'service' },
  { id: 2, title: 'Hosts', url: 'host' },
  { id: 3, title: 'Hosts - Components', url: 'host_component' },
];

const [main, license, config, m_status, m_import, actions, services, hosts, host_components] = all;

const components = {
  id: 8,
  title: 'Components',
  url: 'component',
};

export const Config = {
  menu: {
    cluster: [main, config, m_status, m_import, actions, services, hosts, host_components].sort((a, b) => a.id - b.id),
    service: [main, components, config, m_status, m_import, actions],
    host: [main, config, m_status, actions],
    provider: [main, config, actions],
    bundle: [main, license],
    servicecomponent: [main, config, m_status, actions],
  },
};

@Injectable()
export class NavigationService {
  findIssue = (url: string, issue: Issue) => Object.keys(issue).some((p) => p === url || (IssueSet[url] && IssueSet[url].some((a) => a === p)));
  getIssueMessage = (flag: boolean) => (flag ? ISSUE_MESSAGE : '');

  getLeft(current: Partial<ApiBase>): INavItem[] {
    const getMenu = (c: Partial<ApiBase>) => {
      const forJob = (job: Job) => [main, ...job.log_files.map((a) => ({
        title: `${a.name} [ ${a.type} ]`,
        url: `${a.id}`,
        action: () => (location.href = a.download_url)
      }))];
      const def = (typeName: TypeName, issue: Issue, status: number) =>
        Config.menu[typeName].map((i: INavItem) => ({
          ...i,
          issue: this.findIssue(i.url, issue),
          status,
        }));
      return c.typeName === 'job' ? forJob(c as Job) : def(c.typeName, c.issue || {}, +c.status);
    };
    return getMenu(current);
  }

  getTop(current: IDetails): INavItem[] {
    const issue = (i: Issue) => (isIssue(i) ? ISSUE_MESSAGE : '');
    const link = (p: { typeName: string; id: number }) => (p ? `/${p.typeName}/${p.id}` : '');
    const typeObj = (type: string, prev: string) => ({ url: `${prev}/${type}`, title: `${type}s` });
    const fullLink = (c: { parent?: Cluster; typeName: TypeName; id: number; name: string; issue: Issue }): INavItem[] => [
      typeObj(c.typeName === 'job' ? 'task' : c.typeName, link(c.parent)),
      {
        id: c.id,
        url: `${link(c.parent)}${link(c)}`,
        title: c.name,
        issue: issue(c.issue),
      },
    ];
    return [current.parent, current].reduce((p, c) => [...p, ...(c ? fullLink(c) : [])], []);
  }
}

import { Subject } from 'rxjs';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class PreloaderService {
  private _counter = 0;
  private isFreese = false;

  active$: Subject<boolean> = new Subject<boolean>();

  get counter(): number {
    return this._counter;
  }

  start(): void {
    this._counter++;
    if (!this.isFreese) this.active$.next(this._counter > 0);
    this.isFreese = false;
  }

  end(): void {
    this._counter--;
    this.active$.next(this._counter > 0);
  }

  freeze(): void {
    this.isFreese = true;
  }
}

