import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { Action, createAction, createFeatureSelector, createReducer, createSelector, on, props } from '@ngrx/store';
import { exhaustMap, map } from 'rxjs/operators';

import { IUser, ProfileService } from './profile.service';

export type ProfileState = IUser;

const InitState = {
  username: '',
  change_password: '',
  profile: {
    textarea: {},
    settingsSaved: false,
  },
};

export const loadProfile = createAction('[Profile] Load');
export const clearProfile = createAction('[Profile] ClearProfile');
export const loadProfileSuccess = createAction('[Profile] LoadSuccess', props<{ profile: IUser }>());
export const setTextareaHeight = createAction('[Profile] SetTextareaHeight', props<{ key: string; value: number }>());
export const settingsSave = createAction('[Profile] SettingsSave', props<{ isSet: boolean }>());

const reducer = createReducer(
  InitState,
  on(loadProfileSuccess, (state, { profile }) => ({ ...profile })),
  on(setTextareaHeight, state => ({ ...state })),
  on(settingsSave, (state, { isSet }) => ({ ...state, isSet })),
  on(clearProfile, () => InitState)
);

export function profileReducer(state: ProfileState, action: Action) {
  return reducer(state, action);
}

@Injectable()
export class ProfileEffects {
  load$ = createEffect(() =>
    this.actions$.pipe(
      ofType(loadProfile),
      exhaustMap(() => this.service.getProfile().pipe(map(user => loadProfileSuccess({ profile: user }))))
    )
  );

  saveTextarea$ = createEffect(() =>
    this.actions$.pipe(
      ofType(setTextareaHeight),
      exhaustMap(a =>
        this.service.setTextareaProfile(a).pipe(map(user => loadProfileSuccess({ profile: user })))
      )
    )
  );

  saveSettings$ = createEffect(() =>
    this.actions$.pipe(
      ofType(settingsSave),
      map(a => this.service.setUser('settingsSaved', a.isSet)),
      exhaustMap(() => this.service.setProfile().pipe(map(user => loadProfileSuccess({ profile: user }))))
    )
  );

  constructor(private actions$: Actions, private service: ProfileService) {}
}

export const getProfileSelector = createFeatureSelector<ProfileState>('profile');

export const getProfile = createSelector(
  getProfileSelector,
  state => state.profile
);

export const getFirstAdminLogin = createSelector(
  getProfileSelector,
  state => state.username === 'admin' && !state.profile.settingsSaved
);

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { environment } from '@env/environment';
import { Observable, throwError } from 'rxjs';
import { map, tap } from 'rxjs/operators';

import { AuthService } from '@app/core/auth/auth.service';

const PROFILE_LINK = `${environment.apiRoot}profile/`;

export interface LastViewedTask {
  id: number;
}

export interface IProfile {
  textarea: { [key: string]: number };
  settingsSaved: boolean;
  lastViewedTask?: LastViewedTask;
}

export interface IUser {
  username: string;
  change_password: string;
  profile: IProfile;
}

@Injectable({ providedIn: 'root' })
export class ProfileService {
  private user: IUser;

  constructor(private http: HttpClient, private auth: AuthService) {}

  public getProfile(): Observable<IUser> {
    const source$ = this.http.get<IUser>(`${PROFILE_LINK}${this.auth.auth.login}/`).pipe(
      map(user => (!user.profile ? { ...user, profile: this.emptyProfile() } : user)),
      tap(user => (this.user = user))
    );
    return this.auth.auth.login ? source$ : throwError('Not authorized!');
  }

  emptyProfile() {
    return { textarea: {}, settingsSaved: false };
  }

  setUser(key: string, value: string | boolean | { [key: string]: number }) {
    const profile = { ...this.user.profile };
    profile[key] = value;
    this.user = { ...this.user, profile };
  }

  setProfile(): Observable<IUser> {
    const { username, profile } = { ...this.user };
    return this.http.patch<IUser>(`${PROFILE_LINK}${this.user.username}/`, { username, profile });
  }

  setTextareaProfile(data: { key: string; value: number }): Observable<IUser> {
    const textarea = { ...this.user.profile.textarea };
    textarea[data.key] = data.value;
    this.setUser('textarea', textarea);
    return this.setProfile();
  }

  setLastViewedTask(id: number): Observable<IUser> {
    this.setUser('lastViewedTask', { id });
    return this.setProfile();
  }

  addUser(user: { username: string; profile: string }): Observable<IUser> {
    return this.http.post<IUser>(`${PROFILE_LINK}`, user);
  }

  setPassword(password: string) {
    return this.http.patch(this.user.change_password, { password });
  }
}

import { Injectable } from '@angular/core';
import { HttpRequest, HttpResponse } from '@angular/common/http';

const maxAge = Number.MAX_VALUE; // maximum cache age (ms)

interface RequestCacheEntry {
  url: string;
  response: HttpResponse<any>;
  lastRead: number;
}

export abstract class RequestCache {
  abstract get(req: HttpRequest<any>): HttpResponse<any> | undefined;
  abstract put(req: HttpRequest<any>, response: HttpResponse<any>): void;
}

@Injectable()
export class RequestCacheService implements RequestCache {
  cache = new Map<string, RequestCacheEntry>();

  constructor() {}

  get(req: HttpRequest<any>): HttpResponse<any> | undefined {
    const url = req.urlWithParams;
    const cached = this.cache.get(url);

    if (!cached) return undefined;

    const isExpired = cached.lastRead < Date.now() - maxAge;

    const expired = isExpired ? 'Expired ' : '';
    // this.messanger.add(new Message(`${expired}cached response for "${url}".`));

    return isExpired ? undefined : cached.response;
  }

  put(req: HttpRequest<any>, response: HttpResponse<any>): void {
    const url = req.urlWithParams;
    // this.messanger.add(new Message(`Caching response from "${url}".` ));

    const entry = { url, response, lastRead: Date.now() };
    this.cache.set(url, entry);

    const expired = Date.now() - maxAge;

    this.cache.forEach(c => {
      if (c.lastRead < expired) this.cache.delete(c.url);
    });

    // this.messanger.add(new Message(`Request cache size: ${this.cache.size}.`));
  }
}

import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { Location } from '@angular/common';
import { Effect, Actions, ofType } from '@ngrx/effects';
import { map, tap } from 'rxjs/operators';
import * as RouterActions from './router.actions';

@Injectable()
export class RouterEffects {
  @Effect({ dispatch: false })
  navigate$ = this.actions$.pipe(
    ofType(RouterActions.GO),
    map((action: RouterActions.Go) => action.payload),
    tap(({ path, query: queryParams, extras }) => this.router.navigate(path, { queryParams, ...extras }))
  );

  @Effect({ dispatch: false })
  navigateBack$ = this.actions$.pipe(ofType(RouterActions.BACK), tap(() => this.location.back()));

  @Effect({ dispatch: false })
  navigateForward$ = this.actions$.pipe(ofType(RouterActions.FORWARD), tap(() => this.location.forward()));

  constructor(private actions$: Actions, private router: Router, private location: Location) {}
}

import { Injectable } from '@angular/core';
import { AbstractControl, FormArray, FormControl, FormGroup, ValidatorFn } from '@angular/forms';
import { isObject } from '@app/core/types/func';

import { FieldService } from '../field.service';
import { IFieldOptions, TNForm, TNReq, TValue } from '../types';
import { IYContainer, IYField } from '../yspec/yspec.service';

export interface IValue {
  [key: string]: TValue;
}

export interface IControl {
  name: string;
  type: TNForm;
  rules: IYField | IYContainer | (IYField | IYContainer)[];
  form: FormGroup | FormArray;
  value: IValue | TValue;
  parent: TNReq;
}

@Injectable()
export class SchemeService {
  constructor(private service: FieldService) {}

  emptyValidator() {
    // const isEmptyArray = (v: any) => (Array.isArray(v) && v.length ? v.some((a) => isEmptyValue(a)) : false);
    // const isEmptyObj = (v: any) => (isObject(v) && Object.keys(v).length ? Object.keys(v).some((a) => isEmptyValue(v[a])) : false);
    const isEmptyValue = (v: any) => !v || (Array.isArray(v) && !v.length) || (isObject(v) && !Object.keys(v).length);
    return (): ValidatorFn => (control: AbstractControl): { [key: string]: any } | null => (isEmptyValue(control.value) ? { isEmpty: true } : null);
  }

  setCurrentForm(type: TNForm, parent: FormGroup, field: IFieldOptions) {
    const v = field.required ? this.emptyValidator()() : null;
    const current = type === 'list' || type === 'dict' ? (type === 'list' ? new FormArray([], v) : new FormGroup({}, v)) : new FormControl('', v);
    parent.setControl(field.name, current);
    return current;
  }

  addControlsDict(name: string, source: TValue | IValue, currentForm: FormArray, rules: IYContainer[]): IControl {
    const value = !source ? rules.reduce((p, c) => ({ ...p, [c.name]: '' }), {}) : source;

    const checkValue = () => {
      if (Array.isArray(rules)) {
        if (Array.isArray(value)) {
          return rules.some((a) => a.name === value[0]);
        } else if (typeof value === 'object') {
          return Object.keys(value).every((x) => rules.some((a) => a.name === x));
        }
      }
    };

    if (checkValue()) {
      const form = new FormGroup({});
      currentForm.push(form);
      return { name, value, type: 'dict', rules, form, parent: 'list' };
    }
  }

  addControls(name: string, value: TValue | IValue, currentForm: FormGroup | FormArray, opt: IYContainer | IYField | (IYContainer | IYField)[], type: TNReq): IControl {
    const rules = Array.isArray(opt) ? opt.find((a) => a.name === name) : opt;
    if (!rules) return;
    let form = currentForm;
    if (rules.type !== 'list' && rules.type !== 'dict') {
      const { validator, controlType } = rules as IYField;
      if (Array.isArray(currentForm.controls)) {
        name = currentForm.controls.length.toString();
        (currentForm as FormArray).push(new FormControl(value || '', this.service.setValidator({ validator, controlType })));
      } else (currentForm as FormGroup).addControl(rules.name, new FormControl(rules.type !== 'bool' ? value || '' : value, this.service.setValidator({ validator, controlType })));
    } else {
      form = rules.type === 'list' ? new FormArray([]) : new FormGroup({});
      (currentForm as FormGroup).addControl(rules.name, form);
    }

    return { name, value, type: rules.type, rules, form, parent: type };
  }
}

