import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { exhaustMap } from 'rxjs/operators';

import { socketInit } from './socket.reducer';
import { SocketService } from './socket.service';

@Injectable()
export class SocketEffect {
  init$ = createEffect(
    () =>
      this.actions$.pipe(
        ofType(socketInit),
        exhaustMap(() => this.service.init())
      ),
    { dispatch: false }
  );

  constructor(private service: SocketService, private actions$: Actions) {}
}

import { Directive, Injectable, OnDestroy } from '@angular/core';
import { EventMessage, getMessage, SocketState, clearMessages } from '@app/core/store';
import { select, Store } from '@ngrx/store';
import { filter, tap } from 'rxjs/operators';
import { BaseDirective } from './base.directive';

@Directive({
  selector: '[appBase]',
})
@Injectable()
export class SocketListenerDirective extends BaseDirective implements OnDestroy {
  socket$ = this.socket.pipe(this.takeUntil(), select(getMessage), filter(m => !!m && !!m.object));

  constructor(private socket: Store<SocketState>) {
    super();
  }

  ngOnDestroy() {
    super.ngOnDestroy();
    this.socket.dispatch(clearMessages());
  }

  startListenSocket(): void {
    this.socket$.pipe(
      tap(m => this.socketListener(m))
    ).subscribe();
  }

  socketListener(m: EventMessage) {
    console.warn('No implemented socketListener method', m);
  }
}

import { Injectable } from '@angular/core';
import { AuthService } from '@app/core/auth/auth.service';
import { environment } from '@env/environment';
import { Store } from '@ngrx/store';
import { EMPTY, of } from 'rxjs';

import { socketClose, socketOpen, socketResponse, SocketState, StatusType } from './socket.reducer';

@Injectable({
  providedIn: 'root',
})
export class SocketService {
  connectUrl = environment.SERVER_URL_EVENT;
  socket: WebSocket;

  constructor(private store: Store<SocketState>, private auth: AuthService) {}

  init(openStatus: StatusType = 'open') {
    if (!this.auth.token) {
      console.warn('Socket can not connect. Token is failed.');
      return EMPTY;
    }

    this.socket = new WebSocket(this.connectUrl, ['adcm', `${this.auth.token}`]);

    this.socket.onopen = () => this.store.dispatch(socketOpen({ status: openStatus }));
    this.socket.onclose = () => this.store.dispatch(socketClose({ status: 'close' }));
    this.socket.onmessage = (response: { data: string }) => this.store.dispatch(socketResponse({ message: JSON.parse(response.data) }));

    return of(this.socket);
  }
}

import { Injectable } from '@angular/core';
import { ApiService } from '@app/core/api';
import { ApiState, getStack } from '@app/core/store';
import { Bundle } from '@app/core/types';
import { environment } from '@env/environment';
import { select, Store } from '@ngrx/store';
import { combineLatest, Observable } from 'rxjs';
import { filter, map, mergeMap, switchMap } from 'rxjs/operators';
import { ListResult } from '@app/models/list-result';

export type StackInfo = 'cluster' | 'host' | 'provider' | 'service' | 'bundle';

const UPLOAD_URL = `${environment.apiRoot}stack/upload/`,
  LOAD_URL = `${environment.apiRoot}stack/load/`;

@Injectable({ providedIn: 'root' })
export class StackService {
  constructor(private api: ApiService, private store: Store<ApiState>) {}

  fromStack<T>(name: StackInfo, param?: { [key: string]: string | number }): Observable<T[]> {
    const params = Object.keys(param).reduce<any>((p, c) => ({ ...p, [c]: param[c] }), {});
    return this.store.pipe(
      select(getStack),
      filter((a) => a && !!Object.keys(a).length),
      switchMap((s) => this.api.get<ListResult<T>>(s[name], params).pipe(map((a) => a.results)))
    );
  }

  upload(output: FormData[]) {
    const item = (form: FormData) => {
      return this.api.post(UPLOAD_URL, form).pipe(
        mergeMap(() => this.api.post<Bundle>(LOAD_URL, { bundle_file: (form.get('file') as File).name }))
      );
    };
    return combineLatest(output.map((o) => item(o)));
  }
}

