import { Directive, HostListener, Input } from '@angular/core';
import { MatDialog, MatDialogConfig } from '@angular/material/dialog';
import { IAction } from '@app/core/types';

import { DialogComponent } from '../dialog.component';
import { ActionMasterComponent as component } from './master/master.component';

export interface ActionParameters {
  cluster?: {
    id: number;
    hostcomponent: string;
  };
  actions: IAction[];
}

@Directive({
  selector: '[appActions]'
})
export class ActionsDirective {
  @Input('appActions') inputData: ActionParameters;

  constructor(private dialog: MatDialog) {}

  @HostListener('click')
  onClick() {
    this.dialog.closeAll();
    const dialogModel: MatDialogConfig = this.prepare();
    this.dialog.open(DialogComponent, dialogModel);
  }

  prepare(): MatDialogConfig {
    const maxWidth = '1400px';
    const model = this.inputData;

    if (!model.actions?.length) return { data: { title: 'No parameters for run the action', model: null, component: null } };

    const act = model.actions[0];
    const isMulty = model.actions.length > 1;

    const width = isMulty || act.config?.config.length || act.hostcomponentmap?.length ? '90%' : '400px';
    const title = act.ui_options?.disclaimer ? act.ui_options.disclaimer : isMulty ? 'Run an actions?' : `Run an action [ ${act.display_name} ]?`;

    return {
      width,
      maxWidth,
      data: {
        title,
        model,
        component,
      }
    };
  }
}

import { Directive, OnInit } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { IColumns, IListResult, Paging, RowEventData } from '@adwp-ui/widgets';
import { Sort } from '@angular/material/sort';
import { PageEvent } from '@angular/material/paginator';

import { ListDirective } from './list.directive';
import { AdwpBaseListDirective } from './adwp-base-list.directive';
import { Entities } from '@app/core/types';

@Directive({
  selector: '[appAdwpList]',
})
export abstract class AdwpListDirective<T> extends ListDirective implements OnInit {

  abstract listColumns: IColumns<T>;

  data$: BehaviorSubject<IListResult<T>> = new BehaviorSubject(null);

  paging: BehaviorSubject<Paging> = new BehaviorSubject<Paging>(null);
  sorting: BehaviorSubject<Sort> = new BehaviorSubject<Sort>(null);

  defaultSort: Sort = { active: 'id', direction: 'desc' };

  reload(data: IListResult<Entities>) {
    this.data$.next(data as any);
  }

  ngOnInit() {
    this.baseListDirective = new AdwpBaseListDirective(this, this.service, this.store, this.api);
    this.baseListDirective.typeName = this.type;
    this.baseListDirective.reload = this.reload.bind(this);
    (this.baseListDirective as AdwpBaseListDirective).paging = this.paging;
    (this.baseListDirective as AdwpBaseListDirective).sorting = this.sorting;
    this.baseListDirective.init();
  }

  clickRow(data: RowEventData) {
    this.clickCell(data.event, 'title', data.row);
  }

  auxclickRow(data: RowEventData) {
    this.clickCell(data.event, 'new-tab', data.row);
  }

  changeCount(count: number) {}

  getPageIndex(): number {
    return this.paging.value.pageIndex - 1;
  }

  getPageSize(): number {
    return this.paging.value.pageSize;
  }

  onChangePaging(paging: Paging): void {
    this.paging.next(paging);

    const pageEvent = new PageEvent();
    pageEvent.pageIndex = this.getPageIndex();
    pageEvent.length = this.data$.value.count;
    pageEvent.pageSize = this.getPageSize();

    this.pageHandler(pageEvent);
  }

  onChangeSort(sort: Sort): void {
    this.sorting.next(sort);
    this.changeSorting(sort);
  }

  getSort(): Sort {
    return this.sorting.value;
  }

}

import { Directive, OnDestroy } from '@angular/core';
import { EventMessage } from '@app/core/store';
import { MonoTypeOperatorFunction, Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';

@Directive({
  selector: '[appBase]'
})
export class BaseDirective implements OnDestroy {
  destroy$ = new Subject();

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  takeUntil<T>(): MonoTypeOperatorFunction<T> {
    return takeUntil<T>(this.destroy$);
  }
}
export interface ISocketListener {
  socketListener: (m: EventMessage) => void;
}

