import { Directive, Renderer2, Host, OnInit, Output, EventEmitter } from '@angular/core';
import { MatSelect } from '@angular/material/select';

const POINT_WHEN_EMMIT = 100;

@Directive({
  selector: '[appInfinityScroll]',
})
export class InfinityScrollDirective implements OnInit {
  @Output() topScrollPoint = new EventEmitter();

  constructor(private renderer: Renderer2, @Host() private el: MatSelect) {}

  ngOnInit(): void {
    if ('openedChange' in this.el) this.el.openedChange.subscribe((open: boolean) => this.registerPanel(open));
    else this.renderer.listen(this.el, 'scroll', this.onScrollPanel.bind(this));
  }

  registerPanel(open: boolean) {
    if (open) {
      const panel = (this.el as MatSelect).panel.nativeElement;
      this.renderer.listen(panel, 'scroll', this.onScrollPanel.bind(this));
    }
  }

  onScrollPanel(event: any) {
    const target = event.target;

    const height = Array.from<HTMLElement>(target.children).reduce((p, c) => p + c.clientHeight, 0) - target.clientHeight;
    if (target.scrollTop > height - POINT_WHEN_EMMIT) this.topScrollPoint.emit();
  }
}

import { Directive, EventEmitter, Input, OnDestroy, OnInit, Output } from '@angular/core';
import { filter } from 'rxjs/operators';
import { Store } from '@ngrx/store';
import { ActivatedRoute, Router } from '@angular/router';
import { MatPaginator, PageEvent } from '@angular/material/paginator';
import { MatSort, Sort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';
import { MatDialog } from '@angular/material/dialog';
import { BaseDirective, EventHelper } from '@adwp-ui/widgets';

import { EmmitRow, TypeName } from '@app/core/types';
import { BaseListDirective } from '@app/shared/components/list/base-list.directive';
import { SocketState } from '@app/core/store';
import { ListService } from '@app/shared/components/list/list.service';
import { DialogComponent } from '@app/shared/components';
import { StatusData } from '@app/components/columns/status-column/status-column.component';
import { ICluster } from '@app/models/cluster';
import { ApiService } from '@app/core/api';

enum Direction {
  '' = '',
  'asc' = '',
  'desc' = '-',
}

@Directive({
  selector: '[appAbstractList]',
})
export abstract class ListDirective extends BaseDirective implements OnInit, OnDestroy {

  @Input() type: TypeName;

  baseListDirective: BaseListDirective;

  current: any = {};

  @Input()
  columns: Array<string>;

  @Output()
  listItemEvt = new EventEmitter<EmmitRow>();

  paginator: MatPaginator;

  sort: MatSort;

  data: MatTableDataSource<any> = new MatTableDataSource([]);

  @Output() pageEvent = new EventEmitter<PageEvent>();

  addToSorting = false;

  @Input()
  set dataSource(data: { results: any; count: number }) {
    if (data) {
      const list = data.results;
      this.data = new MatTableDataSource<any>(list);
      this.changeCount(data.count);
      this.listItemEvt.emit({ cmd: 'onLoad', row: list[0] });
    }
  }

  sortParam = '';

  constructor(
    protected service: ListService,
    protected store: Store<SocketState>,
    public route: ActivatedRoute,
    public router: Router,
    public dialog: MatDialog,
    protected api: ApiService,
  ) {
    super();
  }

  changeCount(count: number) {
    this.paginator.length = count;
  }

  clickCell($e: MouseEvent, cmd?: string, row?: any, item?: any) {
    EventHelper.stopPropagation($e);
    this.current = row;
    this.listItemEvt.emit({ cmd, row, item });
  }

  ngOnInit() {
    this.baseListDirective = new BaseListDirective(this, this.service, this.store);
    this.baseListDirective.typeName = this.type;
    this.baseListDirective.init();
  }

  ngOnDestroy() {
    this.baseListDirective.destroy();
  }

  delete($event: MouseEvent, row: any) {
    EventHelper.stopPropagation($event);
    this.dialog
      .open(DialogComponent, {
        data: {
          title: `Deleting  "${row.name || row.fqdn}"`,
          text: 'Are you sure?',
          controls: ['Yes', 'No'],
        },
      })
      .beforeClosed()
      .pipe(filter((yes) => yes))
      .subscribe(() => this.listItemEvt.emit({ cmd: 'delete', row }));
  }

  getPageIndex(): number {
    return this.paginator.pageIndex;
  }

  getPageSize(): number {
    return this.paginator.pageSize;
  }

  changeSorting(sort: Sort) {
    const _filter = this.route.snapshot.paramMap.get('filter') || '';
    const pageIndex = this.getPageIndex();
    const pageSize = this.getPageSize();
    const ordering = this.getSortParam(sort);

    this.router.navigate(
      [
        './',
        {
          page: pageIndex,
          limit: pageSize,
          filter: _filter,
          ordering,
        },
      ],
      { relativeTo: this.route }
    );

    this.sortParam = ordering;
  }

  getSortParam(a: Sort) {
    const penis: { [key: string]: string[] } = {
      prototype_version: ['prototype_display_name', 'prototype_version'],
    };

    if (a) {
      const dumb = penis[a.active] ? penis[a.active] : [a.active],
        active = dumb.map((b: string) => `${Direction[a.direction]}${b}`).join(',');

      const current = this.sortParam;
      if (current && this.addToSorting) {
        const result = current
          .split(',')
          .filter((b) => dumb.every((d) => d !== b.replace('-', '')))
          .join(',');
        return [result, a.direction ? active : ''].filter((e) => e).join(',');
      }

      return a.direction ? active : '';
    } else {
      return '';
    }
  }

  getSort(): Sort {
    return this.sort;
  }

  pageHandler(pageEvent: PageEvent) {
    this.pageEvent.emit(pageEvent);
    localStorage.setItem('limit', String(pageEvent.pageSize));
    const f = this.route.snapshot.paramMap.get('filter') || '';
    const ordering = this.getSortParam(this.getSort());
    this.router.navigate(['./', { page: pageEvent.pageIndex, limit: pageEvent.pageSize, filter: f, ordering }], {
      relativeTo: this.route,
    });
  }

  gotoStatus(data: StatusData<ICluster>) {
    this.clickCell(data.event, data.action, data.row);
  }

}

import { Directive, ElementRef, HostListener, Input, OnDestroy, OnInit } from '@angular/core';
import { getProfile, ProfileService, ProfileState, setTextareaHeight } from '@app/core/store';
import { Store } from '@ngrx/store';

import { BaseDirective } from './base.directive';

// textarea Material
const PADDING_TOP_BOTTOM = 16;

@Directive({
  selector: '[appMTextarea]',
  providers: [ProfileService],
})
export class MTextareaDirective extends BaseDirective implements OnInit, OnDestroy {
  flag = false;
  @Input('appMTextarea')
  key: string;

  constructor(private el: ElementRef, private profile: Store<ProfileState>) {
    super();
  }

  @HostListener('mousedown')
  mouseDown() {
    this.flag = true;
  }

  ngOnInit(): void {
    window.addEventListener('mouseup', () => {
      this.profile
        .select(getProfile)
        .pipe(this.takeUntil())
        .subscribe(p => {
          const data = p.textarea;
          const old = data[this.key];
          const value = +this.el.nativeElement.offsetHeight - PADDING_TOP_BOTTOM;
          if (this.flag && old !== value) this.profile.dispatch(setTextareaHeight({ key: this.key, value }));
          this.flag = false;
        });
    });

    this.profile
      .select(getProfile)
      .pipe(this.takeUntil())
      .subscribe(p => {
        const k = Object.keys(p.textarea).find(key => key === this.key);
        if (k) (this.el.nativeElement as HTMLTextAreaElement).style.height = p.textarea[k] + 'px';
      });
  }
}

import { Directive, ElementRef, EventEmitter, HostListener, Input, Output, Renderer2 } from '@angular/core';

@Directive({
  selector: '[appMultiSort]',
})
export class MultiSortDirective {
  shiftKey = false;
  private params: string[] = [];

  @Input('appMultiSort') set sortParam(param: string) {
    const el = this.el.nativeElement;
    this.params = param.split(',');
    this.hideAllArrows(el);
    setTimeout(() => this.params.map((p) => this.preCell(p, el)), 0);
  }

  @Output() mousedownevent = new EventEmitter<boolean>();

  @HostListener('mousedown', ['$event']) onmousedown(e: MouseEvent) {
    this.shiftKey = e.shiftKey;
    this.mousedownevent.emit(e.shiftKey);
  }

  @HostListener('mouseover', ['$event.target']) onmouseover(h: HTMLElement) {
    const el = this.el.nativeElement;
    this.params.map((p) => this.preCell(p, el));
  }

  @HostListener('mouseout', ['$event.target']) mouseleave(row: HTMLElement) {
    const el = this.el.nativeElement;
    setTimeout(() => this.params.map((p) => this.preCell(p, el)), 300);
  }

  constructor(private el: ElementRef, private renderer: Renderer2) {}

  hideAllArrows(el: any) {
    Array.from<HTMLElement>(el.getElementsByTagName('mat-header-cell')).forEach((e) => {
      const a = e.querySelector('div.mat-sort-header-container>div.mat-sort-header-arrow');
      if (a) this.renderer.setStyle(a, 'opacity', 0);
    });
  }

  preCell(p: string, el: HTMLElement) {
    const direction = p[0] === '-' ? 'descending' : 'ascending',
      active = p[0] === '-' ? p.substr(1) : p;

    const column = el.querySelector(`mat-header-cell.mat-column-${active}`) || el.querySelector(`mat-header-cell[mat-sort-header="${active}"]`);
    if (p && column) {
      this.renderer.setAttribute(column, 'aria-sort', direction);

      const container = column.querySelector('div.mat-sort-header-container');
      this.renderer.addClass(container, 'mat-sort-header-sorted');

      const arrow = container.querySelector('div.mat-sort-header-arrow');
      if (arrow) {
        this.renderer.setStyle(arrow, 'opacity', 1);
        this.renderer.setStyle(arrow, 'transform', 'translateY(0px)');

        const indicator = arrow.querySelector('div.mat-sort-header-indicator');
        this.renderer.setStyle(indicator, 'transform', direction === 'descending' ? 'translateY(10px)' : 'translateY(0px)');

        if (direction === 'descending') {
          const left = indicator.querySelector('.mat-sort-header-pointer-left');
          this.renderer.setStyle(left, 'transform', 'rotate(45deg)');

          const right = indicator.querySelector('.mat-sort-header-pointer-right');
          this.renderer.setStyle(right, 'transform', 'rotate(-45deg)');
        }
      }
    }
  }
}

import { Directive } from '@angular/core';
import { BaseDirective } from '@adwp-ui/widgets';

