import { Component } from '@angular/core';
import { MAT_CHECKBOX_DEFAULT_OPTIONS } from '@angular/material/checkbox';

import { FieldDirective } from './field.directive';

const options = { clickAction: 'noop', color: 'accent' };

@Component({
  selector: 'app-fields-boolean',
  template: `
    <ng-container [formGroup]="form">
      <mat-checkbox [labelPosition]="'before'" [formControlName]="field.name" [indeterminate]="field.value === null" (click)="cbChange()"></mat-checkbox>
      <mat-error *ngIf="!isValid"><app-error-info [field]="field" [control]="control"></app-error-info></mat-error>
    </ng-container>
  `,
  styles: [':host {height: 58px;} mat-error { font-size: 0.75em; margin-left: 14px; }'],
  providers: [{ provide: MAT_CHECKBOX_DEFAULT_OPTIONS, useValue: options }]
})
export class BooleanComponent extends FieldDirective {
  cbChange() {
    if (this.field.read_only) return;
    const tape = this.field.validator.required ? [true, false] : [null, true, false];
    this.field.value = tape[(tape.indexOf(this.field.value as boolean) + 1) % tape.length];
    this.control.setValue(this.field.value);
  }
}

import { Component, Input, OnInit, ViewChild } from '@angular/core';
import { FormControl } from '@angular/forms';
import { Prototype, StackBase } from '@app/core/types';
import { of } from 'rxjs';
import { filter, map, switchMap } from 'rxjs/operators';
import { EventHelper } from '@adwp-ui/widgets';

import { AddService } from '../add-component/add.service';
import { ButtonUploaderComponent } from './button-uploader.component';
import { InputComponent } from './input.component';

@Component({
  selector: 'app-bundles',
  template: `
    <div class="row" [formGroup]="form">
      <mat-form-field>
        <mat-select appInfinityScroll (topScrollPoint)="getNextPage()" required placeholder="Bundle" formControlName="display_name">
          <mat-option value="">...</mat-option>
          <mat-option *ngFor="let bundle of bundles" [value]="bundle.display_name"> {{ bundle.display_name }} </mat-option>
        </mat-select>
      </mat-form-field>
      &nbsp;&nbsp;
      <mat-form-field>
        <mat-select placeholder="Version" required formControlName="bundle_id">
          <mat-option *ngFor="let bundle of versions" [value]="bundle.bundle_id"> {{ bundle.version }} {{ bundle.bundle_edition }} </mat-option>
        </mat-select>
      </mat-form-field>

      <app-button-uploader
        [style.fontSize.px]="24"
        #uploadBtn
        [color]="'accent'"
        [asIcon]="true"
        [label]="'Upload bundles'"
        (click)="EventHelper.stopPropagation($event)"
        (output)="upload($event)"
      ></app-button-uploader>
    </div>
  `,
  styles: ['.row { align-items: center;display:flex; }', 'mat-form-field {flex: 1}'],
})
export class BundlesComponent extends InputComponent implements OnInit {
  EventHelper = EventHelper;

  @Input() typeName: 'cluster' | 'provider';
  @ViewChild('uploadBtn', { static: true }) uploadBtn: ButtonUploaderComponent;
  loadedBundle: { bundle_id: number; display_name: string };
  bundles: StackBase[] = [];
  versions: StackBase[];
  page = 1;
  limit = 50;
  disabledVersion = true;

  constructor(private service: AddService) {
    super();
  }

  ngOnInit(): void {
    this.form.addControl('display_name', new FormControl());
    this.form.addControl('bundle_id', new FormControl());

    this.getBundles();

    this.form
      .get('display_name')
      .valueChanges.pipe(
        this.takeUntil(),
        switchMap((value) => (value ? this.service.getPrototype(this.typeName, { page: 0, limit: 500, ordering: '-version', display_name: value }) : of([])))
      )
      .subscribe((a) => {
        this.versions = a;
        this.selectOne(a, 'bundle_id');
        this.loadedBundle = null;
      });

    // for check license agreement
    this.form
      .get('bundle_id')
      .valueChanges.pipe(
        this.takeUntil(),
        filter((a) => a)
      )
      .subscribe((a) => {
        this.service.currentPrototype = this.versions.find((b) => b.bundle_id === +a);
        this.form.get('prototype_id').setValue(this.service.currentPrototype.id);
      });
  }

  getNextPage() {
    const count = this.bundles.length;
    if (count === this.page * this.limit) {
      this.page++;
      this.getBundles();
    }
  }

  getBundles() {
    const offset = (this.page - 1) * this.limit;
    const params = { fields: 'display_name', distinct: 1, ordering: 'display_name', limit: this.limit, offset };
    this.service.getPrototype(this.typeName, params).subscribe((a) => {
      this.bundles = [...this.bundles, ...a];
      this.selectOne(a, 'display_name');
    });
  }

  selectOne(a: Partial<Prototype>[] = [], formName: string) {
    const el = this.loadedBundle ? a.find((e) => e[formName] === this.loadedBundle[formName]) : null;
    const id = el ? el[formName] : a.length ? (formName === 'bundle_id' || a.length === 1 ? a[0][formName] : '') : '';
    this.form.get(formName).setValue(id);
  }

  upload(data: FormData[]) {
    this.service
      .upload(data)
      .pipe(map((a) => a.map((e) => ({ bundle_id: e.id, display_name: e.display_name, version: e.version }))))
      .subscribe((a) => {
        this.loadedBundle = a[0];
        this.uploadBtn.fileUploadInput.nativeElement.value = '';
        this.page = 0;
        this.bundles = [];
        this.getBundles();
      });
  }
}

import { Component, Input, EventEmitter, Output } from '@angular/core';
import { ThemePalette } from '@angular/material/core';

@Component({
  selector: 'app-button-spinner',
  template: `<button mat-raised-button 
  [color]="color"
  [disabled]="disabled"
  (mousedown)="send()"
  (keyup.enter)="send()">
    <mat-spinner diameter="24" class="spinner" *ngIf="_showSpinner"></mat-spinner>
    {{ title }}
</button>`,
  styles: ['.spinner {position: relative; bottom: 5px; display: inline; }'],
})
export class ButtonSpinnerComponent {
  @Input() title: string;
  @Input() color: ThemePalette;
  @Input() disabled: boolean;
  @Input()
  set spinner(flag) {
    this._showSpinner = flag;
  }
  @Output() clickHandler = new EventEmitter();

  _showSpinner = false;
  private _timer: any;

  send() {
    this.showSpinner();
    this.clickHandler.emit(this);    
    this._timer = setTimeout(() => this.hideSpinner(), 5000);
  }

  public hideSpinner() {
    this.disabled = false;
    this._showSpinner = false;
    clearTimeout(this._timer);
  }

  public showSpinner() {
    this.disabled = true;
    this._showSpinner = true;
  }
}

import { Component, ElementRef, EventEmitter, Input, Output, ViewChild } from '@angular/core';
import { CanColor } from '@angular/material/core';

@Component({
  selector: 'app-button-uploader',
  template: `
    <button *ngIf="!asIcon" mat-raised-button [color]="color" (click)="show()"><mat-icon>cloud_upload</mat-icon> {{ label }}</button>
    <button *ngIf="asIcon" mat-icon-button [color]="color" (click)="show()" [matTooltip]="label"><mat-icon>cloud_upload</mat-icon></button>
    <input
      type="file"
      #fileUploadInput
      multiple="multiple"
      accept=".tar, .tar.gz, .tgz"
      value="upload_bundle_file"
      style="display: none;"
      (change)="fileUploadHandler($event.target)"
    />
  `
})
export class ButtonUploaderComponent {
  @Input() color: CanColor;
  @Input() label: string;
  @Input() asIcon = false;

  @ViewChild('fileUploadInput', { static: true }) fileUploadInput: ElementRef;

  @Output() output = new EventEmitter<FormData[]>();

  show() {
    this.fileUploadInput.nativeElement.click();
  }

  fileUploadHandler(fu: HTMLInputElement) {
    let output: FormData[] = [];
    for (let i = 0; i < fu.files.length; i++) {
      const file = fu.files.item(i);
      const form = new FormData();
      form.append('file', file, file.name);
      output.push(form);
    }
    this.output.emit(output);
    this.fileUploadInput.nativeElement.value = '';
  }
}

