import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { FormControl } from '@angular/forms';
import { BaseDirective } from '@app/shared/directives/base.directive';

import { CompareConfig } from '../types';

@Component({
  selector: 'app-history',
  template: `
    <mat-toolbar>
      <mat-form-field>
        <mat-select #vs placeholder="History" (valueChange)="changeVersion($event); vs.value = ''">
          <mat-option *ngFor="let item of compareConfig; trackBy: trackById" [value]="item.id">[#{{ item.id }}] - {{ item.date | date: 'short' }} {{ item.description }} </mat-option>
        </mat-select>
      </mat-form-field>
      <span>&nbsp;&nbsp;</span>
      <mat-form-field>
        <mat-select placeholder="Compare to" [formControl]="comparator" multiple>
          <mat-option *ngFor="let item of compareConfig; trackBy: trackById" [value]="item.id" [appColorOption]="item.color">
            [#{{ item.id }}] - {{ item.date | date: 'short' }} {{ item.description }}
          </mat-option>
        </mat-select>
      </mat-form-field>
    </mat-toolbar>
  `,
  styles: [':host {height: 64px;}', 'mat-form-field {flex: auto; margin: 0 10px; font-size: 14px; }'],
})
export class HistoryComponent extends BaseDirective implements OnInit {
  @Input() compareConfig: CompareConfig[];
  @Output() version = new EventEmitter<number>();
  @Output() compare = new EventEmitter<number[]>();

  comparator = new FormControl();

  ngOnInit() {
    this.comparator.valueChanges.pipe(this.takeUntil()).subscribe((ids: number[]) => this.compare.emit(ids));
  }

  changeVersion(id: number) {
    this.version.emit(id);
  }

  trackById(item: CompareConfig): number {
    return item.id;
  }

  reset() {
    this.compareConfig = [];
    this.comparator.reset();
  }
}

import { Component, EventEmitter, OnDestroy, OnInit, Output, ViewChild } from '@angular/core';
import { MatCheckbox } from '@angular/material/checkbox';
import { MatSelectionList } from '@angular/material/list';
import { MatPaginator, PageEvent } from '@angular/material/paginator';
import { openClose } from '@app/core/animations';
import { Host } from '@app/core/types';

import { BaseFormDirective } from './base-form.directive';
import { HostComponent } from './host.component';
import { DisplayMode } from './provider.component';

@Component({
  selector: 'app-add-host2cluster',
  template: `
    <div [@openClose]="showForm" [style.overflow]="'hidden'">
      <app-add-host #form [displayMode]="1"></app-add-host>
      <app-add-controls [title]="'Create and add'" [disabled]="!form.form.valid"
                        (cancel)="!Count ? onCancel() : (showForm = false)" (save)="save()"></app-add-controls>
    </div>

    <div [@openClose]="!showForm" [style.overflow]="'hidden'">
      <div class="tools">
        <div class="text">All</div>
        <div class="actions">
          <button
            mat-icon-button
            (click)="showForm = !showForm"
            [color]="showForm ? 'primary' : 'accent'"
            [matTooltip]="showForm ? 'Hide the form for creating and adding a host' : 'Show the form for creating and adding a host'"
          >
            <mat-icon>{{ showForm ? 'clear' : 'add' }}</mat-icon>
          </button>
          &nbsp;
          <mat-checkbox [checked]="false" (click)="selectAllHost(cb.checked)" #cb
                        [matTooltip]="cb.checked ? 'Deselect all' : 'Select all'"></mat-checkbox>
        </div>
      </div>
      <mat-selection-list class="add-host2cluster" #listHosts>
        <mat-list-option *ngFor="let host of list" [value]="host.id" [appTooltip]="host.fqdn"
                         [appTooltipShowByCondition]="true">
          {{ host.fqdn }}
        </mat-list-option>
      </mat-selection-list>
      <mat-paginator *ngIf="Count" [length]="Count" [pageSizeOptions]="[10, 25, 50, 100]"
                     (page)="pageHandler($event)"></mat-paginator>
      <app-add-controls *ngIf="Count" [title]="'Add'" [disabled]="!listHosts?._value?.length" (cancel)="onCancel()"
                        (save)="addHost2Cluster(listHosts._value)"></app-add-controls>
    </div>
  `,
  styleUrls: ['host2cluster.component.scss'],
  animations: [openClose],
})
export class Host2clusterComponent extends BaseFormDirective implements OnInit, OnDestroy {
  list: Host[] = [];
  showForm = false;
  Count = 0;
  displayMode: DisplayMode = DisplayMode.default;
  @Output() event = new EventEmitter();
  @ViewChild('form') hostForm: HostComponent;
  @ViewChild('listHosts') listHosts: MatSelectionList;
  @ViewChild('cb') allCbx: MatCheckbox;
  @ViewChild(MatPaginator) paginator: MatPaginator;

  ngOnInit() {
    this.getAvailableHosts();
  }

  getAvailableHosts(pageIndex = 0, pageSize = 10) {
    this.service
      .getListResults<Host>('host', { limit: pageSize, page: pageIndex, cluster_is_null: 'true' })
      .pipe(this.takeUntil())
      .subscribe((r) => {
        this.Count = r.count;
        this.showForm = !r.count;
        this.displayMode = r.count > 0 ? 2 : 1;
        this.list = r.results;
        if (this.listHosts?.options.length) this.allCbx.checked = false;
      });
  }

  selectAllHost(flag: boolean) {
    if (!flag) this.listHosts.selectAll();
    else this.listHosts.deselectAll();
  }

  save() {
    if (this.hostForm.form.valid) {
      const host = this.hostForm.form.value;
      host.cluster_id = this.service.Cluster.id;
      this.service
        .addHost(host)
        .pipe(this.takeUntil())
        .subscribe((a) => {
          this.hostForm.form.controls['fqdn'].setValue('');
          this.event.emit(`Host [ ${a.fqdn} ] has been added successfully.`);
        });
    }
  }

  addHost2Cluster(value: number[]) {
    this.service
      .addHostInCluster(value.filter((a) => !!a))
      .pipe(this.takeUntil())
      .subscribe(() => {
        this.paginator.firstPage();
        this.getAvailableHosts();
      });
  }

  pageHandler(pageEvent: PageEvent) {
    this.getAvailableHosts(pageEvent.pageIndex, pageEvent.pageSize);
  }
}

