import { Component, OnInit } from '@angular/core';

import { FieldDirective } from './field.directive';
import { debounceTime } from 'rxjs/operators';

@Component({
  selector: 'app-fields-json',
  template: `
    <ng-container [formGroup]="form">
      <mat-form-field>
        <textarea matInput [appMTextarea]="field.key" [formControlName]="field.name" [readonly]="field.read_only"></textarea>
        <mat-error *ngIf="!isValid"><app-error-info [field]="field" [control]="control"></app-error-info></mat-error>
      </mat-form-field>
    </ng-container>
  `,
})
export class JsonComponent extends FieldDirective implements OnInit {
  ngOnInit() {
    super.ngOnInit();
    const control = this.form.controls[this.field.name];
    control.valueChanges.pipe(debounceTime(500)).subscribe((value) => {
      try {
        const v = JSON.parse(value);
        control.setValue(JSON.stringify(v, undefined, 4));
      } catch (e) {}
    });
  }
}

import { Component, Input } from '@angular/core';

import { ApiBase, Issue } from '@app/core/types';
import { NavigationService, INavItem } from '../navigation.service';

@Component({
  selector: 'app-details-left',
  template: `
    <mat-nav-list>
      <a mat-list-item
         [appForTest]="'tab_' + item.url"
         *ngFor="let item of items"
         [routerLink]="[item.url]"
         routerLinkActive="active"
      >
        <span>{{ item.title }}</span>&nbsp;

        <button mat-icon-button
                *ngIf="item.action"
                color="primary"
                (click)="btnClick(item.action)"
        >
          <mat-icon>cloud_download</mat-icon>
        </button>

        <mat-icon *ngIf="item.issue" color="warn">priority_hight</mat-icon>

        <mat-icon *ngIf="item.url === 'status'"
                  [color]="item.status === 0 ? 'accent' : 'warn'"
        >
          {{ item.status === 0 ? 'check_circle_outline' : 'error_outline' }}
        </mat-icon>
      </a>
    </mat-nav-list>
  `,
  styles: ['mat-nav-list {padding-top: 20px;}', 'a span { white-space: nowrap; }'],
})
export class LeftComponent {
  items: INavItem[] = [];
  @Input() set current(c: Partial<ApiBase>) {
    if (c) this.items = this.navigation.getLeft(c);
  }

  @Input() set issue(i: Issue) {
    if (i) this.items = this.items.map((a) => ({ ...a, issue: this.navigation.findIssue(a.url, i) ? 'issue' : '' }));
  }

  @Input() set status(v: number) {
    const b = this.items.find((a) => a.url === 'status');
    if (b) b.status = v;
  }

  constructor(private navigation: NavigationService) {}

  btnClick(action: () => void) {
    action();
  }
}

import { Component, ComponentRef, OnInit, ViewChild } from '@angular/core';
import { Store } from '@ngrx/store';
import { ActivatedRoute, Router } from '@angular/router';
import { MatDialog } from '@angular/material/dialog';
import { IColumns } from '@adwp-ui/widgets';

import { StackService } from '../../core/services';
import { ClusterService } from '@app/core/services/cluster.service';
import { AdwpListDirective } from '@app/abstract-directives/adwp-list.directive';
import { ListService } from '@app/shared/components/list/list.service';
import { SocketState } from '@app/core/store';
import { TypeName } from '@app/core/types';
import { IBundle } from '@app/models/bundle';
import { ListFactory } from '@app/factories/list-factory';
import { EditionColumnComponent } from '@app/components/columns/edition-column/edition-column.component';
import { ApiService } from '@app/core/api';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-stack',
  template: `
    <mat-toolbar class="toolbar">
      <app-crumbs [navigation]="[{ url: '/bundle', title: 'bundles' }]"></app-crumbs>
      <app-button-uploader #uploadBtn [color]="'accent'" [label]="'Upload bundles'"
                           (output)="upload($event)"></app-button-uploader>
    </mat-toolbar>

    <adwp-list
      [columns]="listColumns"
      [dataSource]="data$ | async"
      [paging]="paging | async"
      [sort]="sorting | async"
      [defaultSort]="defaultSort"
      [currentId]="current ? current.id : undefined"
      (clickRow)="clickRow($event)"
      (auxclickRow)="auxclickRow($event)"
      (changePaging)="onChangePaging($event)"
      (changeSort)="onChangeSort($event)"
    ></adwp-list>
  `,
  styles: [':host { flex: 1; }'],
})
export class StackComponent extends AdwpListDirective<IBundle> {

  type: TypeName = 'bundle';

  listColumns = [
    ListFactory.nameColumn(),
    {
      label: 'Version',
      sort: 'version',
      value: row => row.version,
    },
    {
      label: 'Edition',
      type: 'component',
      component: EditionColumnComponent,
      instanceTaken: (componentRef: ComponentRef<EditionColumnComponent>) => {
        componentRef.instance.onClick
          .pipe(this.takeUntil())
          .subscribe(
            (data: { event: MouseEvent, action: string, row: any }) =>
              this.clickCell(data.event, data.action, data.row)
          );
      }
    },
    ListFactory.descriptionColumn(),
    ListFactory.deleteColumn(this),
  ] as IColumns<IBundle>;

  @ViewChild('uploadBtn', { static: true }) uploadBtn: any;

  constructor(
    private stack: StackService,
    protected service: ListService,
    protected store: Store<SocketState>,
    public route: ActivatedRoute,
    public router: Router,
    public dialog: MatDialog,
    protected api: ApiService,
  ) {
    super(service, store, route, router, dialog, api);
  }

  upload(data: FormData[]) {
    this.stack.upload(data).subscribe();
  }

}

