    <pre>{{ text | async }}</pre>
  `,
  styles: [':host {width: 100%; max-width: 960px}']
})
export class LicenseComponent implements OnInit {
  text: Observable<string>;

  constructor(private service: ClusterService) {}

  ngOnInit() {
    this.text = this.service.getBundleLicenseText();
  }

}

