import { Component, Input, OnInit, EventEmitter, Output, OnDestroy } from '@angular/core';
import { clearEmptyField, Provider } from '@app/core/types';

import { BaseFormDirective } from './base-form.directive';
import { Subscription } from 'rxjs';

export enum DisplayMode {
  default,
  inHost,
  noCluster
}

@Component({
  selector: 'app-add-provider',
  template: `
    <ng-container [formGroup]="form">
      <app-bundles [form]="form" [typeName]="'provider'"></app-bundles>
      <ng-container *ngIf="displayMode === 0; else asHost">
        <app-input [form]="form" [label]="'Hostprovider name'" [controlName]="'name'" [isRequired]="true"></app-input>
        <app-input [form]="form" [label]="'Description'" [controlName]="'description'"></app-input>
        <app-add-controls [disabled]="!form.valid" (cancel)="onCancel()" (save)="save()"></app-add-controls>
      </ng-container>
      <ng-template #asHost>
        <div class="row">
          <mat-form-field class="full-width">
            <input required matInput placeholder="Hostprovider name" formControlName="name" />
            <button [style.fontSize.px]="24" [disabled]="!form.valid" matTooltip="Create hostprovider" matSuffix mat-icon-button [color]="'accent'" (click)="save()">
              <mat-icon>add_box</mat-icon>
            </button>
            <mat-error *ngIf="form.get('name').hasError('required')">Hostprovider name is required </mat-error>
          </mat-form-field>
        </div>
      </ng-template>
    </ng-container>
  `,
  styles: ['.row {display: flex;}'],
})
export class ProviderComponent extends BaseFormDirective implements OnInit, OnDestroy {
  sgn: Subscription;
  @Input() displayMode: DisplayMode = DisplayMode.default;
  @Output() cancel = new EventEmitter();

  ngOnInit() {
    this.form = this.service.model('provider').form;
    this.sgn = this.service.genName(this.form);
  }

  ngOnDestroy() {
    this.sgn.unsubscribe();
  }

  save() {
    const data = clearEmptyField(this.form.value);
    this.service
      .add<Provider>(data, 'provider')
      .pipe(this.takeUntil())
      .subscribe((x) => {
        if (this.displayMode === 0) this.onCancel();
        else {
          this.form.reset();
          this.cancel.emit(x.id);
        }
      });
  }
}

import { Component, Input, OnInit } from '@angular/core';
import { FormArray, FormGroup } from '@angular/forms';

import { TNReq, TValue } from '../types';
import { IYContainer, IYField } from '../yspec/yspec.service';
import { IControl, IValue, SchemeService } from './scheme.service';

@Component({
  selector: 'app-root-scheme',
  templateUrl: './root.component.html',
  styleUrls: ['./root.component.scss'],
})
export class RootComponent implements OnInit {
  @Input() form: FormGroup | FormArray;
  @Input() options: IYContainer | IYField;
  @Input() value: TValue;
  @Input() isReadOnly = false;

  controls: IControl[] = [];

  constructor(private scheme: SchemeService) {}

  init() {
    if (this.value) {
      if (this.options.type === 'list' && Array.isArray(this.value)) (this.value as IValue[]).map((x, i) => this.add(['', x]));
      else if (typeof this.value === 'object') Object.keys(this.value).map((x) => this.add([x, this.value[x]]));
    } else if (this.options.type === 'dict' && Array.isArray(this.options.options)) {
      this.options.options.map((x) => this.add([x.name, '']));
    }
  }

  ngOnInit(): void {
    this.init();
  }

  reload(value: TValue) {
    this.value = value;
    this.controls = [];
    if (Array.isArray(this.form.controls)) {
      this.form.controls.forEach((v, i) => (this.form as FormArray).removeAt(i));
    }
    this.init();
  }

  add(v: [string, IValue | TValue] = ['', '']) {
    const [name, value] = v;
    const flag = (this.rules as IYContainer).type === 'dict';
    const item = flag
      ? this.scheme.addControlsDict(name, value, this.form as FormArray, this.itemRules as IYContainer[])
      : this.scheme.addControls(name, value, this.form, this.rules, this.options.type as TNReq);
    this.controls = [...this.controls, item];
  }

  showControls() {
    return !this.isReadOnly && (this.options.type === 'list' || this.options.type === 'dict');
  }

  remove(name: string | number) {
    if (Array.isArray(this.form.controls)) {
      (this.form as FormArray).removeAt(+name);
      this.controls = this.controls.filter((a, i) => (a.name ? a.name !== name : i !== +name));
    }
  }

  get rules(): IYField | IYContainer | (IYField | IYContainer)[] {
    if ('options' in this.options) return this.options.options;
    else return this.options;
  }

  get itemRules(): (IYField | IYContainer)[] {
    return (this.rules as IYContainer).options as (IYField | IYContainer)[];
  }
}

