import { Component, OnChanges, OnInit, SimpleChanges, ViewChild } from '@angular/core';
import { AbstractControl } from '@angular/forms';
import { FieldDirective } from '@app/shared/form-elements/field.directive';

import { TNReq } from '../types';
import { IYContainer, IYField, YspecService } from '../yspec/yspec.service';
import { RootComponent } from './root.component';
import { SchemeService } from './scheme.service';

@Component({
  selector: 'app-scheme',
  styles: [
    `
      div.main {
        flex: 1;
      }
      .error {
        display: block;
        margin: -20px 0 6px 10px;
      }
    `,
  ],
  template: `<div class="main">
    <app-root-scheme #root [isReadOnly]="field.read_only" [form]="current" [options]="rules" [value]="field.value || field.default"></app-root-scheme>
    <mat-error *ngIf="hasError('isEmpty')" class="error">Field [{{ field.display_name }}] is required!</mat-error>
  </div>`,
})
export class SchemeComponent extends FieldDirective implements OnInit, OnChanges {
  rules: IYField | IYContainer;
  current: AbstractControl;

  @ViewChild('root') root: RootComponent;

  constructor(private yspec: YspecService, private scheme: SchemeService) {
    super();
  }

  /**
   * after saving, the link between the form and the current (form) is lost
   * TODO: eliminate
   */
  ngOnChanges(changes: SimpleChanges): void {
    if (!changes.form.firstChange) {
      this.field.limits.rules = this.rules;
      this.form.setControl(this.field.name, this.current);
    }
  }

  ngOnInit() {
    this.yspec.Root = this.field.limits.yspec;
    this.rules = this.yspec.build();
    this.field.limits.rules = this.rules;
    this.rules.name = '';
    this.current = this.scheme.setCurrentForm(this.rules.type as TNReq, this.form, this.field);
  }

  /** this is using for restore default value */
  reload() {
    this.root.reload(this.field.default);
  }
}

import { Component, EventEmitter, OnDestroy, OnInit, Output } from '@angular/core';
import { FormControl } from '@angular/forms';
import { BaseDirective } from '@app/shared/directives/base.directive';
import { debounceTime, distinctUntilChanged } from 'rxjs/operators';

@Component({
  selector: 'app-search',
  template: `
    <mat-form-field class="seach-field">
      <input matInput type="text" placeholder="Search field" [formControl]="search" id="config_search_input" />
      <button mat-button matSuffix mat-icon-button aria-label="Search"><mat-icon>search</mat-icon></button>
      <button mat-button matSuffix mat-icon-button aria-label="Clear" (click)="search.setValue('')">
        <mat-icon>close</mat-icon>
      </button>
    </mat-form-field>
  `,
  styles: ['mat-form-field {flex: auto;margin: 0 10px;font-size: 14px;}'],
})
export class SearchComponent extends BaseDirective implements OnInit, OnDestroy {
  search = new FormControl();
  @Output() pattern = new EventEmitter<string>();

  constructor() {
    super();
  }

  ngOnInit() {
    this.search.valueChanges.pipe(this.takeUntil(), debounceTime(300), distinctUntilChanged()).subscribe((value) => this.pattern.emit(value));
  }
}

import { Component, OnInit, ViewChild } from '@angular/core';
import { MatSelectionList, MatSelectionListChange } from '@angular/material/list';
import { SelectOption } from '@app/core/types';
import { Observable } from 'rxjs';

import { BaseFormDirective } from './base-form.directive';

@Component({
  selector: 'app-add-service',
  template: `
    <ng-container *ngIf="options$ | async as protos">
      <mat-selection-list #listServices (selectionChange)="selectAll($event)">
        <mat-list-option *ngIf="protos.length">All</mat-list-option>
        <mat-list-option *ngFor="let proto of protos" [value]="proto">
          {{ proto.name }}
        </mat-list-option>
      </mat-selection-list>
      <app-add-controls *ngIf="protos.length; else not" [title]="'Add'" [disabled]="!form.valid" (cancel)="onCancel()" (save)="save()"></app-add-controls>
    </ng-container>
    <ng-template #not>
      <p>
        <i>
          There are no new services. You cluster already has all of them.
        </i>
      </p>
    </ng-template>
  `
})
export class ServiceComponent extends BaseFormDirective implements OnInit {
  options$: Observable<SelectOption[]>;
  @ViewChild('listServices')
  private listServices: MatSelectionList;

  ngOnInit() {
    this.options$ = this.service.getProtoServiceForCurrentCluster();
  }


  selectAll(e: MatSelectionListChange) {
    if (!e.option.value) {
      if (e.option.selected) this.listServices.selectAll();
      else this.listServices.deselectAll();
    }
  }

  save() {
    const result = this.listServices.selectedOptions.selected.filter(a => a.value).map(a => ({ prototype_id: +a.value.id }));
    this.service
      .addService(result)
      .pipe(this.takeUntil())
      .subscribe(() => this.dialog.closeAll());
  }
}

