import { Component, Input } from '@angular/core';
import { Cluster, IAction, isIssue, Issue } from '@app/core/types';
import { UpgradeItem } from '@app/shared/components';

import { IDetails, INavItem, NavigationService } from '../navigation.service';

@Component({
  selector: 'app-details-top',
  template: `
      <app-crumbs [navigation]="items"></app-crumbs>
      <app-upgrade *ngIf="upgradable" [row]="upgrade" xPosition="after"></app-upgrade>
      <div [style.flex]="1"></div>
      <app-action-list *ngIf="actionFlag" [asButton]="true" [actionLink]="actionLink" [actions]="actions" [state]="state" [disabled]="disabled" [cluster]="cluster"></app-action-list>
      <!-- <app-actions [source]="actions || []" [isIssue]="eIssue" [cluster]="cluster"></app-actions> -->
  `,
  styles: [':host {display: flex;width: 100%;padding-right: 10px;}', 'app-action-list {display: block; margin-top: 2px;}'],
})
export class TopComponent {
  items: INavItem[];
  cluster: { id: number; hostcomponent: string };
  disabled: boolean;
  state: string;
  upgrade: UpgradeItem;
  actionLink: string;
  actionFlag = false;
  @Input() upgradable: boolean;
  @Input() actions: IAction[] = [];

  @Input() set isIssue(v: boolean) {
    this.disabled = v;
    if (this.upgrade) this.upgrade.issue = (v ? { issue: '' } : {}) as Issue;
    if (this.items) {
      const a = this.items.find((b) => b.id);
      if (a) a.issue = this.navigation.getIssueMessage(v);
    }
  }

  @Input() set current(c: IDetails) {
    if (c) {
      const exclude = ['bundle', 'job'];
      this.actionFlag = !exclude.includes(c.typeName);
      this.items = this.navigation.getTop(c);
      const { id, hostcomponent, issue, upgradable, upgrade } = c.parent || (c as Partial<Cluster>);
      this.cluster = { id, hostcomponent };
      this.actionLink = c.action;
      this.upgradable = upgradable;
      this.disabled = isIssue(issue) || c.state === 'locked';
      this.state = c.state;
      this.upgrade = { issue, upgradable, upgrade };
    }
  }
  constructor(private navigation: NavigationService) {}
}

import { Component, EventEmitter, Input, OnChanges, Output, SimpleChanges } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { ApiService } from '@app/core/api';
import { EmmitRow, Issue, isIssue } from '@app/core/types';
import { concat, Observable, of } from 'rxjs';
import { filter, map, switchMap } from 'rxjs/operators';
import { EventHelper } from '@adwp-ui/widgets';

import { BaseDirective } from '../directives';
import { DialogComponent } from './dialog.component';

export interface UpgradeItem {
  upgradable: boolean;
  upgrade: string;
  issue: Issue;
}

export interface Upgrade {
  id: number;
  bundle_id: number;
  name: string;
  description: string;
  do: string;
  upgradable: boolean;
  from_edition: string[];
  license: 'unaccepted' | 'absent';
  license_url: string;
  max_strict: boolean;
  max_version: string;
  min_strict: boolean;
  min_version: string;
  state_available: string;
  state_on_success: string;
  url: string;
}

@Component({
  selector: 'app-upgrade',
  template: `
    <ng-container *ngIf="(list$ | async) as upgrades">
      <button *ngIf="!!upgrades.length"
              mat-icon-button
              matTooltip="There are a pending upgrades of object here"
              [appForTest]="'upgrade_btn'"
              color="warn"
              [disabled]="!checkIssue()"
              [matMenuTriggerFor]="menu"
              (click)="EventHelper.stopPropagation($event)"
      >
        <mat-icon>sync_problem</mat-icon>
      </button>
      <mat-menu #menu="matMenu" [overlapTrigger]="false" [xPosition]="xPosition" yPosition="below">
        <ng-template matMenuContent>
          <button *ngFor="let item of upgrades" mat-menu-item (click)="runUpgrade(item)">
            <span>{{ item.name || 'No name' }}</span>
          </button>
        </ng-template>
      </mat-menu>
    </ng-container>
  `
})
export class UpgradeComponent extends BaseDirective implements OnChanges {
  EventHelper = EventHelper;

  list$: Observable<Upgrade[]>;
  pRow: UpgradeItem = { upgradable: false, upgrade: '', issue: null };

  @Input() xPosition = 'before';

  @Input()
  set row(row: UpgradeItem) {
    this.pRow = row;
  }

  @Output()
  refresh: EventEmitter<EmmitRow> = new EventEmitter<EmmitRow>();

  constructor(private api: ApiService, private dialog: MatDialog) {
    super();
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes.row && changes.row.currentValue.upgradable) {
      this.list$ = this.getUpgrades(changes.row.currentValue.upgrade);
    }
  }

  checkIssue() {
    return this.pRow.upgradable && !isIssue(this.pRow.issue);
  }

  runUpgrade(item: Upgrade) {
    const license$ = item.license === 'unaccepted' ? this.api.put(`${item.license_url}accept/`, {}) : of();
    const do$ = this.api.post<{ id: number }>(item.do, {});
    this.fork(item)
      .pipe(
        switchMap(text =>
          this.dialog
            .open(DialogComponent, {
              data: {
                title: 'Are you sure you want to upgrade?',
                text,
                disabled: !item.upgradable,
                controls: item.license === 'unaccepted' ? {
                  label: 'Do you accept the license agreement?',
                  buttons: ['Yes', 'No']
                } : ['Yes', 'No']
              }
            })
            .beforeClosed()
            .pipe(
              this.takeUntil(),
              filter(yes => yes),
              switchMap(() => concat(license$, do$))
            )
        )
      )
      .subscribe(row => this.refresh.emit({ cmd: 'refresh', row }));
  }

  fork(item: Upgrade) {
    const flag = item.license === 'unaccepted';
    return flag ? this.api.get<{ text: string }>(item.license_url).pipe(map(a => a.text)) : of(item.description);
  }

  getUpgrades(upgrade: string): Observable<any> {
    return this.api.get(`${upgrade}?ordering=-name`).pipe(
      filter((list: Upgrade[]) => !!list.length)
    );
  }
}

