variables.defaultValue:
(host_id: number, com: CompTile, action: 'add' | 'remove', load: StatePost): boolean => {
  if (com.actions?.length) {
    if (action === 'remove') return flag(host_id, com, load) ? com.actions.some((a) => a === 'remove') : true;
    if (action === 'add') return flag(host_id, com, load) ? true : com.actions.some((a) => a === 'add');
  } else return true;
}

(c: TConstraint, r: number): boolean => {
  if (!c?.length) return true;
  const v = c[c.length - 1];
  return v === '+' || v === 'odd' || v > r || v === 'depend';
}

{
  menu: {
    cluster: [main, config, m_status, m_import, actions, services, hosts, host_components].sort((a, b) => a.id - b.id),
    service: [main, components, config, m_status, m_import, actions],
    host: [main, config, m_status, actions],
    provider: [main, config, actions],
    bundle: [main, license],
    servicecomponent: [main, config, m_status, actions],
  },
}

(c: CompTile, cs: CompTile[]): CompTile[] => {
  const r =
    c.requires?.reduce<{ prototype_id: number }[]>(
      (p, a) => [...p, ...a.components.map((b) => ({ prototype_id: b.prototype_id }))],
      []
    ) || [];
  return cs.filter((a) => r.some((b) => b.prototype_id === a.prototype_id));
}

() =>
  new FormGroup({
    prototype_id: new FormControl('', Validators.required),
    name: new FormControl('', Validators.required),
    description: new FormControl(),
  })

(a: IFieldStack, d: IConfig): IPanelOptions => ({
  ...a,
  hidden: isHidden(a),
  active: a.activatable ? isActive(d.attr, a.name) : true,
  options: getOptions(a, d),
})

(required: boolean, min: number, max: number, type: TNForm) => ({
  required,
  min,
  max,
  pattern: getPattern(type),
})

(t: TNForm) => {
